const fs = require('fs')
const glob = require('glob')

// Find all .svelte files
const files = glob.sync('**/*.svelte', {
  ignore: ['**/node_modules/**', '**/build/**']
})

let modifiedCount = 0

for (const file of files) {
  let content = fs.readFileSync(file, 'utf8')
  const originalContent = content

  // Add type="button" to buttons that don't have a type attribute
  content = content.replace(/<button(?![^>]*\btype=)([^>]*)>/g, '<button type="button"$1>')

  if (content !== originalContent) {
    fs.writeFileSync(file, content)
    modifiedCount++
    console.log(`Modified: ${file}`)
  }
}

console.log(`\nTotal files modified: ${modifiedCount}`)

import { query } from '../src/lib/database/connection.ts'

// 추가할 직급 데이터
const executivePositions = [
  {
    name: '연구소장',
    description: '연구소 전체를 총괄하고 연구 방향을 설정하는 최고 책임자',
    department: 'PSR팀',
    level: 6 // 최고급 직급
  },
  {
    name: '상무',
    description: '회사 경영진으로 주요 의사결정에 참여하는 임원급 직급',
    department: '경영기획팀',
    level: 6 // 최고급 직급
  }
]

async function addExecutivePositions() {
  try {
    console.log('🔄 Adding executive positions...')

    for (const position of executivePositions) {
      // 중복 체크
      const existingPos = await query(
        'SELECT id FROM positions WHERE LOWER(name) = LOWER($1) AND department = $2',
        [position.name, position.department]
      )

      if (existingPos.rows.length > 0) {
        console.log(`⚠️  Position "${position.name}" already exists in ${position.department}`)
        continue
      }

      // 직급 추가
      const result = await query(
        `
				INSERT INTO positions (name, description, department, level, status, created_at, updated_at)
				VALUES ($1, $2, $3, $4, $5, $6, $7)
				RETURNING id, name, description, department, level, status
			`,
        [
          position.name,
          position.description,
          position.department,
          position.level,
          'active',
          new Date(),
          new Date()
        ]
      )

      console.log(
        `✅ Added position: ${position.name} (${position.department}) - Level ${position.level}`
      )
    }

    // 결과 확인
    console.log('\n🔍 Verifying added positions...')
    const result = await query(`
			SELECT 
				name, description, department, level, status
			FROM positions 
			WHERE name IN ('연구소장', '상무')
			ORDER BY department, level DESC
		`)

    console.log('📋 Executive positions:')
    result.rows.forEach(row => {
      console.log(`  ${row.name} (${row.department}) - Level ${row.level}: ${row.description}`)
    })

    // 전체 직급 구조 확인
    console.log('\n📊 Complete position hierarchy:')
    const allPositions = await query(`
			SELECT 
				department,
				name,
				level
			FROM positions 
			WHERE status = 'active'
			ORDER BY department, level DESC, name ASC
		`)

    let currentDept = ''
    allPositions.rows.forEach(row => {
      if (row.department !== currentDept) {
        console.log(`\n📁 ${row.department}:`)
        currentDept = row.department
      }
      const levelIndicator = '  '.repeat(6 - row.level) + '└─ '
      console.log(`${levelIndicator}${row.name} (Level ${row.level})`)
    })

    console.log('\n✅ Executive positions added successfully!')
  } catch (error) {
    console.error('❌ Failed to add executive positions:', error)
    throw error
  }
}

// Run the script
addExecutivePositions()
  .then(() => {
    console.log('🎉 Executive positions setup completed successfully!')
    process.exit(0)
  })
  .catch(error => {
    console.error('💥 Executive positions setup failed:', error)
    process.exit(1)
  })

import { query } from '../src/lib/database/connection.ts'

// 추가할 새로운 조직들
const newOrganizations = [
  {
    name: '대표',
    description: '회사의 최고 경영진으로 전체 경영을 총괄하는 조직',
    level: 7 // 최고급
  },
  {
    name: '전략기획실',
    description: '회사의 전략 수립 및 기획을 담당하는 조직',
    level: 6 // 고급
  },
  {
    name: '연구소',
    description: '연구개발 및 기술 혁신을 담당하는 조직',
    level: 6 // 고급
  }
]

async function addNewOrganizations() {
  try {
    console.log('🔄 Adding new organizations...')

    for (const org of newOrganizations) {
      // 중복 체크
      const existingOrg = await query('SELECT id FROM positions WHERE LOWER(name) = LOWER($1)', [
        org.name
      ])

      if (existingOrg.rows.length > 0) {
        console.log(`⚠️  Organization "${org.name}" already exists`)
        continue
      }

      // 조직 추가
      const result = await query(
        `
				INSERT INTO positions (name, description, department, level, status, created_at, updated_at)
				VALUES ($1, $2, $3, $4, $5, $6, $7)
				RETURNING id, name, description, department, level, status
			`,
        [
          org.name,
          org.description,
          org.name, // department도 같은 이름으로 설정
          org.level,
          'active',
          new Date(),
          new Date()
        ]
      )

      console.log(`✅ Added organization: ${org.name} - Level ${org.level}`)
    }

    // 결과 확인
    console.log('\n🔍 Verifying added organizations...')
    const result = await query(`
			SELECT 
				name, description, department, level, status
			FROM positions 
			WHERE name IN ('대표', '전략기획실', '연구소')
			ORDER BY level DESC, name ASC
		`)

    console.log('📋 New organizations:')
    result.rows.forEach(row => {
      console.log(`  ${row.name} (${row.department}) - Level ${row.level}: ${row.description}`)
    })

    // 전체 조직 구조 확인
    console.log('\n📊 Complete organization hierarchy:')
    const allPositions = await query(`
			SELECT 
				department,
				name,
				level
			FROM positions 
			WHERE status = 'active'
			ORDER BY 
				CASE department
					WHEN '대표' THEN 1
					WHEN '전략기획실' THEN 2
					WHEN '연구소' THEN 3
					WHEN '부서없음' THEN 999
					ELSE 100
				END,
				level DESC,
				name ASC
		`)

    let currentDept = ''
    allPositions.rows.forEach(row => {
      if (row.department !== currentDept) {
        console.log(`\n📁 ${row.department}:`)
        currentDept = row.department
      }
      const levelIndicator = '  '.repeat(7 - row.level) + '└─ '
      console.log(`${levelIndicator}${row.name} (Level ${row.level})`)
    })

    console.log('\n✅ New organizations added successfully!')
  } catch (error) {
    console.error('❌ Failed to add new organizations:', error)
    throw error
  }
}

// Run the script
addNewOrganizations()
  .then(() => {
    console.log('🎉 New organizations setup completed successfully!')
    process.exit(0)
  })
  .catch(error => {
    console.error('💥 New organizations setup failed:', error)
    process.exit(1)
  })

// Node.js 내장 fetch 사용

const API_BASE = 'http://localhost:5173/api'

async function addNoDepartment() {
  try {
    const response = await fetch(`${API_BASE}/departments`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        name: '부서없음',
        description: '부서가 배정되지 않은 직원들을 위한 부서',
        level: 0, // 최하위 레벨
        is_active: true
      })
    })

    const result = await response.json()
    if (result.success) {
      console.log('✅ 부서없음 부서가 성공적으로 추가되었습니다.')
      console.log(`📋 부서 ID: ${result.data.id}`)
    } else {
      console.log(`❌ 부서 추가 실패: ${result.error}`)
    }
    return result.success
  } catch (error) {
    console.error('Error adding department:', error)
    return false
  }
}

async function main() {
  console.log('🔄 부서없음 부서 추가 시작...')

  const success = await addNoDepartment()

  if (success) {
    console.log('✅ 부서없음 부서 추가 완료!')
  } else {
    console.log('❌ 부서없음 부서 추가 실패!')
  }
}

main().catch(console.error)

#!/usr/bin/env node

// 자동 검증 서비스 CLI 도구

import { exec, spawn } from 'child_process'
import { existsSync, mkdirSync, readFileSync, writeFileSync } from 'fs'
import { join } from 'path'
import { promisify } from 'util'

class AutoValidationCLI {
  constructor() {
    this.serviceFile = 'src/lib/services/auto-validation-service.js'
    this.pidFile = join(process.cwd(), 'logs', 'auto-validation.pid')
    this.logFile = join(process.cwd(), 'logs', 'auto-validation.log')
    this.ensureLogDirectory()
  }

  /**
   * CLI 실행
   */
  async run() {
    const command = process.argv[2]
    const args = process.argv.slice(3)

    switch (command) {
      case 'start':
        await this.start()
        break
      case 'stop':
        await this.stop()
        break
      case 'restart':
        await this.restart()
        break
      case 'status':
        await this.status()
        break
      case 'logs':
        await this.logs(args[0] || '50')
        break
      case 'test':
        await this.test()
        break
      case 'help':
        this.help()
        break
      default:
        console.log('❌ 알 수 없는 명령어입니다.')
        this.help()
    }
  }

  /**
   * 서비스 시작
   */
  async start() {
    if (this.isRunning()) {
      console.log('⚠️ 자동 검증 서비스가 이미 실행 중입니다.')
      return
    }

    console.log('🚀 자동 검증 서비스 시작...')

    try {
      // TypeScript 파일을 JavaScript로 컴파일
      await this.compileService()

      // 백그라운드에서 서비스 실행
      const serviceProcess = spawn('node', [this.serviceFile], {
        detached: true,
        stdio: 'ignore'
      })

      serviceProcess.unref()

      // PID 저장
      writeFileSync(this.pidFile, serviceProcess.pid.toString())

      console.log('✅ 자동 검증 서비스가 시작되었습니다.')
      console.log(`📋 PID: ${serviceProcess.pid}`)
      console.log(`📝 로그: ${this.logFile}`)
      console.log('')
      console.log('사용 가능한 명령어:')
      console.log('  npm run auto-validation status  - 상태 확인')
      console.log('  npm run auto-validation logs    - 로그 확인')
      console.log('  npm run auto-validation stop    - 서비스 중지')
    } catch (error) {
      console.error('❌ 서비스 시작 실패:', error.message)
    }
  }

  /**
   * 서비스 중지
   */
  async stop() {
    if (!this.isRunning()) {
      console.log('⚠️ 자동 검증 서비스가 실행 중이 아닙니다.')
      return
    }

    console.log('🛑 자동 검증 서비스 중지...')

    try {
      const pid = parseInt(readFileSync(this.pidFile, 'utf-8'))
      process.kill(pid, 'SIGTERM')

      // PID 파일 삭제
      writeFileSync(this.pidFile, '')

      console.log('✅ 자동 검증 서비스가 중지되었습니다.')
    } catch (error) {
      console.error('❌ 서비스 중지 실패:', error.message)
    }
  }

  /**
   * 서비스 재시작
   */
  async restart() {
    console.log('🔄 자동 검증 서비스 재시작...')
    await this.stop()
    await new Promise(resolve => setTimeout(resolve, 2000)) // 2초 대기
    await this.start()
  }

  /**
   * 서비스 상태 확인
   */
  async status() {
    console.log('📊 자동 검증 서비스 상태')
    console.log('=' * 40)

    if (this.isRunning()) {
      const pid = parseInt(readFileSync(this.pidFile, 'utf-8'))
      console.log('✅ 상태: 실행 중')
      console.log(`📋 PID: ${pid}`)
      console.log(`📝 로그 파일: ${this.logFile}`)

      // 최근 로그 확인
      if (existsSync(this.logFile)) {
        const logs = readFileSync(this.logFile, 'utf-8').split('\n')
        const recentLogs = logs.slice(-5).filter(log => log.trim())
        console.log('')
        console.log('📝 최근 로그:')
        recentLogs.forEach(log => console.log(`  ${log}`))
      }
    } else {
      console.log('❌ 상태: 중지됨')
    }
  }

  /**
   * 로그 확인
   */
  async logs(lines = '50') {
    if (!existsSync(this.logFile)) {
      console.log('📝 로그 파일이 존재하지 않습니다.')
      return
    }

    console.log(`📝 자동 검증 서비스 로그 (최근 ${lines}줄)`)
    console.log('=' * 50)

    try {
      const { stdout } = await this.execAsync(`tail -n ${lines} "${this.logFile}"`)
      console.log(stdout)
    } catch (error) {
      console.error('❌ 로그 확인 실패:', error.message)
    }
  }

  /**
   * 테스트 실행
   */
  async test() {
    console.log('🧪 자동 검증 서비스 테스트')
    console.log('=' * 40)

    try {
      // 1. 서비스 파일 존재 확인
      console.log('1. 서비스 파일 확인...')
      if (existsSync(this.serviceFile)) {
        console.log('  ✅ 서비스 파일 존재')
      } else {
        console.log('  ❌ 서비스 파일 없음')
        return
      }

      // 2. 의존성 확인
      console.log('2. 의존성 확인...')
      const packageJson = JSON.parse(readFileSync('package.json', 'utf-8'))
      const requiredDeps = ['prettier', 'typescript']
      const missingDeps = requiredDeps.filter(dep => !packageJson.devDependencies?.[dep])

      if (missingDeps.length === 0) {
        console.log('  ✅ 모든 의존성 설치됨')
      } else {
        console.log(`  ❌ 누락된 의존성: ${missingDeps.join(', ')}`)
        console.log(`  💡 설치 명령어: npm install --save-dev ${missingDeps.join(' ')}`)
      }

      // 3. 디렉토리 구조 확인
      console.log('3. 디렉토리 구조 확인...')
      const requiredDirs = [
        'src/lib/utils',
        'src/routes/api/project-management',
        'src/lib/components/project-management'
      ]

      requiredDirs.forEach(dir => {
        if (existsSync(dir)) {
          console.log(`  ✅ ${dir}`)
        } else {
          console.log(`  ❌ ${dir} (없음)`)
        }
      })

      // 4. 권한 확인
      console.log('4. 권한 확인...')
      try {
        writeFileSync(this.logFile, 'test')
        console.log('  ✅ 로그 파일 쓰기 권한')
      } catch (error) {
        console.log('  ❌ 로그 파일 쓰기 권한 없음')
      }

      console.log('')
      console.log('✅ 테스트 완료')
    } catch (error) {
      console.error('❌ 테스트 실패:', error.message)
    }
  }

  /**
   * 도움말 표시
   */
  help() {
    console.log('🤖 자동 검증 서비스 CLI')
    console.log('')
    console.log('사용법: npm run auto-validation <명령어>')
    console.log('')
    console.log('명령어:')
    console.log('  start     - 서비스 시작')
    console.log('  stop      - 서비스 중지')
    console.log('  restart   - 서비스 재시작')
    console.log('  status    - 서비스 상태 확인')
    console.log('  logs [n]  - 로그 확인 (기본: 50줄)')
    console.log('  test      - 서비스 테스트')
    console.log('  help      - 도움말 표시')
    console.log('')
    console.log('예시:')
    console.log('  npm run auto-validation start')
    console.log('  npm run auto-validation status')
    console.log('  npm run auto-validation logs 100')
  }

  /**
   * 서비스가 실행 중인지 확인
   */
  isRunning() {
    if (!existsSync(this.pidFile)) {
      return false
    }

    try {
      const pid = parseInt(readFileSync(this.pidFile, 'utf-8'))
      process.kill(pid, 0) // 프로세스 존재 확인
      return true
    } catch (error) {
      return false
    }
  }

  /**
   * 서비스 파일 컴파일
   */
  async compileService() {
    console.log('🔨 서비스 파일 컴파일...')

    try {
      // TypeScript를 JavaScript로 컴파일
      const { stdout, stderr } = await this.execAsync(
        'npx tsc src/lib/services/auto-validation-service.ts --outDir . --target es2020 --module commonjs'
      )

      if (stderr) {
        console.log('⚠️ 컴파일 경고:', stderr)
      }

      console.log('✅ 컴파일 완료')
    } catch (error) {
      console.error('❌ 컴파일 실패:', error.message)
      throw error
    }
  }

  /**
   * execAsync 헬퍼
   */
  execAsync(command) {
    const execAsync = promisify(exec)
    return execAsync(command)
  }

  /**
   * 로그 디렉토리 생성
   */
  ensureLogDirectory() {
    const logDir = join(process.cwd(), 'logs')
    if (!existsSync(logDir)) {
      mkdirSync(logDir, { recursive: true })
    }
  }
}

// CLI 실행
const cli = new AutoValidationCLI()
cli.run().catch(error => {
  console.error('❌ CLI 실행 실패:', error)
  process.exit(1)
})

#!/usr/bin/env node

/**
 * 자동 오류 체크 스크립트
 * 코드 작성 후 TypeScript 오류와 린터 오류를 자동으로 체크합니다.
 */

import { execSync } from 'child_process'

// 색상 코드
const colors = {
  reset: '\x1b[0m',
  bright: '\x1b[1m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  magenta: '\x1b[35m',
  cyan: '\x1b[36m'
}

function log(message, color = 'reset') {
  console.log(`${colors[color]}${message}${colors.reset}`)
}

function logSection(title) {
  log(`\n${'='.repeat(60)}`, 'cyan')
  log(`${title}`, 'bright')
  log(`${'='.repeat(60)}`, 'cyan')
}

function logSuccess(message) {
  log(`✅ ${message}`, 'green')
}

function logError(message) {
  log(`❌ ${message}`, 'red')
}

function logWarning(message) {
  log(`⚠️  ${message}`, 'yellow')
}

function logInfo(message) {
  log(`ℹ️  ${message}`, 'blue')
}

async function checkTypeScriptErrors() {
  logSection('TypeScript 오류 체크')

  try {
    logInfo('Svelte 타입 체크 실행 중...')
    const result = execSync('npm run check', {
      encoding: 'utf8',
      stdio: 'pipe'
    })

    logSuccess('TypeScript 오류 없음!')
    return { success: true, errors: [] }
  } catch (error) {
    const output = error.stdout || error.stderr || ''
    const lines = output
      .split('\n')
      .filter(
        line => line.includes('error') || line.includes('Error') || line.includes('Type error')
      )

    if (lines.length > 0) {
      logError(`TypeScript 오류 발견 (${lines.length}개):`)
      lines.forEach(line => logError(`  ${line}`))
    } else {
      logWarning('TypeScript 체크 완료 (경고 있음)')
    }

    return { success: false, errors: lines }
  }
}

async function checkLinterErrors() {
  logSection('린터 오류 체크')

  try {
    logInfo('린터 체크 실행 중...')

    // 현재 작업 디렉토리에서 린터 실행
    const result = execSync('npx svelte-check --tsconfig ./tsconfig.json', {
      encoding: 'utf8',
      stdio: 'pipe'
    })

    logSuccess('린터 오류 없음!')
    return { success: true, errors: [] }
  } catch (error) {
    const output = error.stdout || error.stderr || ''
    const lines = output
      .split('\n')
      .filter(
        line =>
          line.includes('warning') ||
          line.includes('Warning') ||
          line.includes('error') ||
          line.includes('Error')
      )

    if (lines.length > 0) {
      logWarning(`린터 경고/오류 발견 (${lines.length}개):`)
      lines.forEach(line => logWarning(`  ${line}`))
    } else {
      logInfo('린터 체크 완료')
    }

    return { success: false, errors: lines }
  }
}

async function checkBuildErrors() {
  logSection('빌드 오류 체크')

  try {
    logInfo('빌드 테스트 실행 중...')

    const result = execSync('npm run build', {
      encoding: 'utf8',
      stdio: 'pipe'
    })

    logSuccess('빌드 성공!')
    return { success: true, errors: [] }
  } catch (error) {
    const output = error.stdout || error.stderr || ''
    const lines = output
      .split('\n')
      .filter(
        line =>
          line.includes('error') ||
          line.includes('Error') ||
          line.includes('failed') ||
          line.includes('Failed')
      )

    if (lines.length > 0) {
      logError(`빌드 오류 발견 (${lines.length}개):`)
      lines.forEach(line => logError(`  ${line}`))
    }

    return { success: false, errors: lines }
  }
}

async function generateErrorReport(tsResult, linterResult, buildResult) {
  logSection('오류 리포트')

  const totalErrors =
    tsResult.errors.length + linterResult.errors.length + buildResult.errors.length

  if (totalErrors === 0) {
    logSuccess('🎉 모든 체크 통과! 코드가 깔끔합니다.')
    return true
  }

  logError(`총 ${totalErrors}개의 문제가 발견되었습니다:`)

  if (tsResult.errors.length > 0) {
    logError(`  - TypeScript 오류: ${tsResult.errors.length}개`)
  }

  if (linterResult.errors.length > 0) {
    logWarning(`  - 린터 경고/오류: ${linterResult.errors.length}개`)
  }

  if (buildResult.errors.length > 0) {
    logError(`  - 빌드 오류: ${buildResult.errors.length}개`)
  }

  logInfo('\n💡 권장사항:')
  logInfo('  1. TypeScript 오류부터 수정하세요')
  logInfo('  2. 빌드 오류를 수정하세요')
  logInfo('  3. 린터 경고는 코드 품질 개선을 위해 수정하세요')

  return false
}

async function main() {
  log('🔍 자동 오류 체크 시작', 'bright')
  log('='.repeat(60), 'cyan')

  try {
    // 1. TypeScript 오류 체크
    const tsResult = await checkTypeScriptErrors()

    // 2. 린터 오류 체크
    const linterResult = await checkLinterErrors()

    // 3. 빌드 오류 체크
    const buildResult = await checkBuildErrors()

    // 4. 리포트 생성
    const allPassed = await generateErrorReport(tsResult, linterResult, buildResult)

    if (allPassed) {
      log('\n🎯 결과: 모든 체크 통과!', 'green')
      process.exit(0)
    } else {
      log('\n⚠️  결과: 문제가 발견되었습니다. 위의 권장사항을 따라 수정하세요.', 'yellow')
      process.exit(1)
    }
  } catch (error) {
    logError(`스크립트 실행 중 오류 발생: ${error.message}`)
    process.exit(1)
  }
}

