    console.error('생성 실패:', error)
    process.exit(1)
  })

#!/usr/bin/env node

/**
 * AWS Database Schema Extractor
 *
 * This script extracts the complete schema from AWS database
 * Usage: node scripts/extract-aws-schema.js
 */

import fs from 'fs'
import { Pool } from 'pg'

// AWS Database Configuration
const awsConfig = {
  host: 'db-viahub.cdgqkcss8mpj.ap-northeast-2.rds.amazonaws.com',
  port: 5432,
  database: 'postgres',
  user: 'postgres',
  password: 'viahubdev',
  ssl: {
    rejectUnauthorized: false
  }
}

class SchemaExtractor {
  constructor() {
    this.awsPool = null
  }

  async connect() {
    console.log('🔗 Connecting to AWS database...')
    try {
      this.awsPool = new Pool(awsConfig)
      await this.awsPool.connect()
      console.log('✅ Connected to AWS database')
    } catch (error) {
      console.error('❌ Failed to connect to AWS database:', error.message)
      throw error
    }
  }

  async getTableDefinitions() {
    console.log('📋 Extracting table definitions...')

    const result = await this.awsPool.query(`
			SELECT 
				table_name,
				column_name,
				data_type,
				is_nullable,
				column_default,
				character_maximum_length,
				numeric_precision,
				numeric_scale,
				ordinal_position
			FROM information_schema.columns 
			WHERE table_schema = 'public' 
			ORDER BY table_name, ordinal_position
		`)

    const tables = {}
    result.rows.forEach(row => {
      if (!tables[row.table_name]) {
        tables[row.table_name] = []
      }
      tables[row.table_name].push(row)
    })

    console.log(`   Found ${Object.keys(tables).length} tables`)
    return tables
  }

  async getIndexes() {
    console.log('🔍 Extracting indexes...')

    const result = await this.awsPool.query(`
			SELECT 
				tablename,
				indexname,
				indexdef
			FROM pg_indexes 
			WHERE schemaname = 'public'
			ORDER BY tablename, indexname
		`)

    console.log(`   Found ${result.rows.length} indexes`)
    return result.rows
  }

  async getConstraints() {
    console.log('🔗 Extracting constraints...')

    const result = await this.awsPool.query(`
			SELECT 
				tc.table_name,
				tc.constraint_name,
				tc.constraint_type,
				kcu.column_name,
				ccu.table_name AS foreign_table_name,
				ccu.column_name AS foreign_column_name
			FROM information_schema.table_constraints AS tc 
			LEFT JOIN information_schema.key_column_usage AS kcu
				ON tc.constraint_name = kcu.constraint_name
				AND tc.table_schema = kcu.table_schema
			LEFT JOIN information_schema.constraint_column_usage AS ccu
				ON ccu.constraint_name = tc.constraint_name
				AND ccu.table_schema = tc.table_schema
			WHERE tc.table_schema = 'public'
			ORDER BY tc.table_name, tc.constraint_type, tc.constraint_name
		`)

    console.log(`   Found ${result.rows.length} constraints`)
    return result.rows
  }

  async getFunctions() {
    console.log('⚙️  Extracting functions...')

    const result = await this.awsPool.query(`
			SELECT 
				routine_name,
				routine_definition
			FROM information_schema.routines 
			WHERE routine_schema = 'public' 
			AND routine_type = 'FUNCTION'
			ORDER BY routine_name
		`)

    console.log(`   Found ${result.rows.length} functions`)
    return result.rows
  }

  async getTriggers() {
    console.log('🎯 Extracting triggers...')

    const result = await this.awsPool.query(`
			SELECT 
				trigger_name,
				event_object_table,
				action_statement
			FROM information_schema.triggers 
			WHERE trigger_schema = 'public'
			ORDER BY event_object_table, trigger_name
		`)

    console.log(`   Found ${result.rows.length} triggers`)
    return result.rows
  }

  async getViews() {
    console.log('👁️  Extracting views...')

    const result = await this.awsPool.query(`
			SELECT 
				table_name,
				view_definition
			FROM information_schema.views 
			WHERE table_schema = 'public'
			ORDER BY table_name
		`)

    console.log(`   Found ${result.rows.length} views`)
    return result.rows
  }

  generateCreateTableSQL(tableName, columns) {
    let sql = `CREATE TABLE ${tableName} (\n`

    const columnDefs = columns.map(col => {
      let def = `  ${col.column_name} ${col.data_type}`

      if (col.character_maximum_length) {
        def += `(${col.character_maximum_length})`
      } else if (col.numeric_precision) {
        def += `(${col.numeric_precision}`
        if (col.numeric_scale) {
          def += `,${col.numeric_scale}`
        }
        def += `)`
      }

      if (col.is_nullable === 'NO') {
        def += ' NOT NULL'
      }

      if (col.column_default) {
        def += ` DEFAULT ${col.column_default}`
      }

      return def
    })

    sql += columnDefs.join(',\n')
    sql += '\n);'

    return sql
  }

  async generateCompleteSchema() {
    console.log('🚀 Generating complete schema...\n')

    const tables = await this.getTableDefinitions()
    const indexes = await this.getIndexes()
    const constraints = await this.getConstraints()
    const functions = await this.getFunctions()
    const triggers = await this.getTriggers()
    const views = await this.getViews()

    let schema = `-- AWS Database Schema Export
-- Generated on: ${new Date().toISOString()}
-- Source: ${awsConfig.host}

-- Enable required extensions
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";
CREATE EXTENSION IF NOT EXISTS "pgcrypto";

`

    // Generate CREATE TABLE statements
    Object.keys(tables).forEach(tableName => {
      schema += `\n-- Table: ${tableName}\n`
      schema += this.generateCreateTableSQL(tableName, tables[tableName])
      schema += '\n\n'
    })

    // Generate indexes
    if (indexes.length > 0) {
      schema += '-- Indexes\n'
      indexes.forEach(index => {
        if (!index.indexname.includes('_pkey')) {
          // Skip primary keys
          schema += `${index.indexdef};\n`
        }
      })
      schema += '\n'
    }

    // Generate constraints
    if (constraints.length > 0) {
      schema += '-- Constraints\n'
      constraints.forEach(constraint => {
        if (constraint.constraint_type === 'FOREIGN KEY') {
          schema += `ALTER TABLE ${constraint.table_name} ADD CONSTRAINT ${constraint.constraint_name} FOREIGN KEY (${constraint.column_name}) REFERENCES ${constraint.foreign_table_name}(${constraint.foreign_column_name});\n`
        }
      })
      schema += '\n'
    }

    // Generate functions
    if (functions.length > 0) {
      schema += '-- Functions\n'
      functions.forEach(func => {
        schema += `-- Function: ${func.routine_name}\n`
        schema += `${func.routine_definition};\n\n`
      })
    }

    // Generate triggers
    if (triggers.length > 0) {
      schema += '-- Triggers\n'
      triggers.forEach(trigger => {
        schema += `CREATE TRIGGER ${trigger.trigger_name}\n`
        schema += `  ON ${trigger.event_object_table}\n`
        schema += `  ${trigger.action_statement};\n\n`
      })
    }

    // Generate views
    if (views.length > 0) {
      schema += '-- Views\n'
      views.forEach(view => {
        schema += `CREATE VIEW ${view.table_name} AS\n`
        schema += `${view.view_definition};\n\n`
      })
    }

    return schema
  }

  async extract() {
    console.log('🚀 Starting AWS schema extraction...\n')

    await this.connect()

    const schema = await this.generateCompleteSchema()

    // Write to file
    const outputFile = 'aws-schema.sql'
    fs.writeFileSync(outputFile, schema)

    console.log(`\n✅ Schema extraction completed!`)
    console.log(`📄 Schema saved to: ${outputFile}`)
    console.log(`📊 Total size: ${(schema.length / 1024).toFixed(2)} KB`)
  }

  async close() {
    if (this.awsPool) {
      await this.awsPool.end()
      console.log('🔌 Disconnected from AWS database')
    }
  }
}

// Main execution
async function main() {
  const extractor = new SchemaExtractor()

  try {
    await extractor.extract()
  } catch (error) {
    console.error('❌ Schema extraction failed:', error)
    process.exit(1)
  } finally {
    await extractor.close()
  }
}

// Run if called directly
if (import.meta.url === `file://${process.argv[1]}`) {
  main().catch(console.error)
}

export { SchemaExtractor }

#!/usr/bin/env node

/**
 * Accessibility 경고 자동 수정 스크립트
 * label과 input/select를 자동으로 연결
 */

import { readFileSync, writeFileSync } from 'fs'
import { glob } from 'glob'

console.log('🔧 Accessibility 경고 자동 수정 시작...')

// 수정할 파일들 찾기
const files = glob.sync('src/**/*.svelte')

let totalFixed = 0

for (const file of files) {
  try {
    let content = readFileSync(file, 'utf8')
    let modified = false

    // label과 input 연결 패턴 찾기
    const labelInputPattern =
      /<label([^>]*)class="([^"]*)"([^>]*)>([^<]*)<\/label>\s*<input\s*([^>]*?)>/g

    content = content.replace(
      labelInputPattern,
      (match, before, className, after, labelText, inputAttrs) => {
        // 고유한 ID 생성
        const id = `input-${Math.random().toString(36).substr(2, 9)}`

        // label에 for 속성 추가
        const newLabel = `<label${before}for="${id}" class="${className}"${after}>${labelText}</label>`

        // input에 id 속성 추가
        const newInput = `<input id="${id}" ${inputAttrs}>`

        modified = true
        return `${newLabel}\n\t\t\t\t\t${newInput}`
      }
    )

    // label과 select 연결 패턴 찾기
    const labelSelectPattern =
      /<label([^>]*)class="([^"]*)"([^>]*)>([^<]*)<\/label>\s*<select\s*([^>]*?)>/g

    content = content.replace(
      labelSelectPattern,
      (match, before, className, after, labelText, selectAttrs) => {
        // 고유한 ID 생성
        const id = `select-${Math.random().toString(36).substr(2, 9)}`

        // label에 for 속성 추가
        const newLabel = `<label${before}for="${id}" class="${className}"${after}>${labelText}</label>`

        // select에 id 속성 추가
        const newSelect = `<select id="${id}" ${selectAttrs}>`

        modified = true
        return `${newLabel}\n\t\t\t\t\t${newSelect}`
      }
    )

    if (modified) {
      writeFileSync(file, content, 'utf8')
      console.log(`✅ ${file} 수정 완료`)
      totalFixed++
    }
  } catch (error) {
    console.error(`❌ ${file} 수정 실패:`, error.message)
  }
}

console.log(`\n🎉 총 ${totalFixed}개 파일의 accessibility 경고 수정 완료!`)

#!/usr/bin/env node

/**
 * Schema Fixer
 *
 * This script fixes common issues in the extracted AWS schema
 * Usage: node scripts/fix-schema.js
 */

import fs from 'fs'

function fixSchema() {
  console.log('🔧 Fixing AWS schema...')

  let schema = fs.readFileSync('aws-schema.sql', 'utf8')

  // Fix integer data types with precision (PostgreSQL doesn't support integer(32))
  schema = schema.replace(/integer\(\d+\)/g, 'integer')
  schema = schema.replace(/bigint\(\d+\)/g, 'bigint')

  // Fix ARRAY type
  schema = schema.replace(/ARRAY,/g, 'text[],')

  // Fix function definitions - add proper function syntax
  schema = schema.replace(
    /CREATE OR REPLACE FUNCTION update_updated_at_column\(\)\s*RETURNS TRIGGER AS \$\$\s*BEGIN\s*NEW\.updated_at = CURRENT_TIMESTAMP;\s*RETURN NEW;\s*END;\s*\$\$ LANGUAGE plpgsql;/g,
    `CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;`
  )

  // Fix trigger definitions
  schema = schema.replace(/CREATE TRIGGER update_[^;]+;\s*null;/g, '')

  // Remove problematic trigger definitions
  schema = schema.replace(/CREATE TRIGGER [^;]+;\s*null;/g, '')

  // Remove duplicate table definitions
  const lines = schema.split('\n')
  const seenTables = new Set()
  const fixedLines = []

  for (const line of lines) {
    const createTableMatch = line.match(/CREATE TABLE (\w+)/)
    if (createTableMatch) {
      const tableName = createTableMatch[1]
      if (seenTables.has(tableName)) {
        console.log(`   ⏭️  Skipping duplicate table: ${tableName}`)
        continue
      }
      seenTables.add(tableName)
    }
    fixedLines.push(line)
  }

  schema = fixedLines.join('\n')

  // Write fixed schema
  fs.writeFileSync('aws-schema-fixed.sql', schema)

  console.log('✅ Schema fixed!')
  console.log('📄 Fixed schema saved to: aws-schema-fixed.sql')
  console.log(`📊 Total size: ${(schema.length / 1024).toFixed(2)} KB`)
}

// Main execution
fixSchema()

#!/usr/bin/env node

/**
 * 코드 품질 게이트 스크립트
 *
 * 이 스크립트는 코드 품질 임계값을 정의하고 체크합니다.
 * CI/CD 파이프라인에서 사용하여 품질 기준을 만족하지 않으면 빌드를 실패시킵니다.
 */

import { execSync } from 'child_process'

// 색상 정의
const colors = {
  reset: '\x1b[0m',
  bright: '\x1b[1m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  magenta: '\x1b[35m',
  cyan: '\x1b[36m'
}

// 품질 게이트 임계값 정의
const QUALITY_GATES = {
  // TypeScript 오류 허용 개수
  MAX_TYPESCRIPT_ERRORS: 0,

  // ESLint 오류 허용 개수
  MAX_ESLINT_ERRORS: 0,
  MAX_ESLINT_WARNINGS: 10,

  // 테스트 커버리지 (백분율)
  MIN_TEST_COVERAGE: 80,

  // 코드 복잡도
  MAX_CYCLOMATIC_COMPLEXITY: 10,

  // 파일 크기 (라인 수)
  MAX_FILE_SIZE: 500,

  // 함수 크기 (라인 수)
  MAX_FUNCTION_SIZE: 50,

  // 중첩 깊이
  MAX_NESTING_DEPTH: 4,

  // 보안 취약점
  MAX_SECURITY_VULNERABILITIES: 0,

  // 의존성 문제
  MAX_DEPENDENCY_ISSUES: 0,

  // 빌드 시간 (초)
  MAX_BUILD_TIME: 120
}

function log(message, color = 'reset') {
  console.log(`${colors[color]}${message}${colors.reset}`)
}

function logSection(title) {
  log(`\n${'='.repeat(60)}`, 'cyan')
  log(`${title}`, 'bright')
  log(`${'='.repeat(60)}`, 'cyan')
}

function logSuccess(message) {
  log(`✅ ${message}`, 'green')
}

function logError(message) {
  log(`❌ ${message}`, 'red')
}

function logWarning(message) {
  log(`⚠️  ${message}`, 'yellow')
}

function logInfo(message) {
  log(`ℹ️  ${message}`, 'blue')
}

class QualityGate {
  constructor() {
    this.results = {
      typescript: { passed: false, errors: 0, warnings: 0 },
      eslint: { passed: false, errors: 0, warnings: 0 },
      build: { passed: false, time: 0 },
      security: { passed: false, vulnerabilities: 0 },
      dependencies: { passed: false, issues: 0 },
      coverage: { passed: false, percentage: 0 },
      complexity: { passed: false, maxComplexity: 0 },
      fileSize: { passed: false, maxSize: 0 },
      overall: { passed: false }
    }
  }

  async checkTypeScript() {
    logSection('TypeScript 품질 체크')

    try {
      const startTime = Date.now()
      const result = execSync('npm run check', {
        encoding: 'utf8',
        stdio: 'pipe'
      })
      const endTime = Date.now()

      logSuccess('TypeScript 체크 통과!')
      this.results.typescript.passed = true
      this.results.typescript.errors = 0
      this.results.typescript.warnings = 0

      return true
    } catch (error) {
      const output = error.stdout || error.stderr || ''
      const errorLines = output
        .split('\n')
        .filter(line => line.includes('error') || line.includes('Error'))
      const warningLines = output
        .split('\n')
        .filter(line => line.includes('warning') || line.includes('Warning'))

      this.results.typescript.errors = errorLines.length
      this.results.typescript.warnings = warningLines.length

      if (this.results.typescript.errors > QUALITY_GATES.MAX_TYPESCRIPT_ERRORS) {
        logError(
          `TypeScript 오류 ${this.results.typescript.errors}개 (허용: ${QUALITY_GATES.MAX_TYPESCRIPT_ERRORS}개)`
        )
        this.results.typescript.passed = false
        return false
      }

      logWarning(`TypeScript 경고 ${this.results.typescript.warnings}개 발견`)
      this.results.typescript.passed = true
      return true
    }
  }

  async checkESLint() {
    logSection('ESLint 품질 체크')

    try {
      const result = execSync('npx eslint src/ --ext .ts,.svelte --format=json', {
        encoding: 'utf8',
        stdio: 'pipe'
      })

      logSuccess('ESLint 체크 통과!')
      this.results.eslint.passed = true
      this.results.eslint.errors = 0
      this.results.eslint.warnings = 0

      return true
    } catch (error) {
      try {
        const output = error.stdout || ''
        const eslintResults = JSON.parse(output)

        let totalErrors = 0
        let totalWarnings = 0

        eslintResults.forEach(file => {
          totalErrors += file.errorCount || 0
          totalWarnings += file.warningCount || 0
        })

        this.results.eslint.errors = totalErrors
        this.results.eslint.warnings = totalWarnings

        if (totalErrors > QUALITY_GATES.MAX_ESLINT_ERRORS) {
          logError(`ESLint 오류 ${totalErrors}개 (허용: ${QUALITY_GATES.MAX_ESLINT_ERRORS}개)`)
          this.results.eslint.passed = false
          return false
        }

        if (totalWarnings > QUALITY_GATES.MAX_ESLINT_WARNINGS) {
          logWarning(
            `ESLint 경고 ${totalWarnings}개 (허용: ${QUALITY_GATES.MAX_ESLINT_WARNINGS}개)`
          )
          this.results.eslint.passed = false
          return false
        }

        logSuccess('ESLint 품질 게이트 통과!')
        this.results.eslint.passed = true
        return true
      } catch (parseError) {
        logError('ESLint 결과 파싱 실패')
        this.results.eslint.passed = false
        return false
      }
    }
  }

  async checkBuild() {
    logSection('빌드 품질 체크')

    try {
      const startTime = Date.now()
      execSync('npm run build', {
        encoding: 'utf8',
        stdio: 'pipe'
      })
      const endTime = Date.now()

      const buildTime = (endTime - startTime) / 1000
      this.results.build.time = buildTime

      if (buildTime > QUALITY_GATES.MAX_BUILD_TIME) {
        logWarning(`빌드 시간 ${buildTime.toFixed(2)}초 (허용: ${QUALITY_GATES.MAX_BUILD_TIME}초)`)
        this.results.build.passed = false
        return false
      }

      logSuccess(`빌드 성공! (${buildTime.toFixed(2)}초)`)
      this.results.build.passed = true
      return true
    } catch (error) {
      logError('빌드 실패')
      this.results.build.passed = false
      return false
    }
  }

  async checkSecurity() {
    logSection('보안 품질 체크')

    try {
      const result = execSync('npm audit --json', {
        encoding: 'utf8',
        stdio: 'pipe'
      })

      const auditResult = JSON.parse(result)
      const vulnerabilities = auditResult.metadata?.vulnerabilities || {}

      let totalVulnerabilities = 0
      Object.values(vulnerabilities).forEach(count => {
        if (typeof count === 'number') {
          totalVulnerabilities += count
        }
      })

      this.results.security.vulnerabilities = totalVulnerabilities

      if (totalVulnerabilities > QUALITY_GATES.MAX_SECURITY_VULNERABILITIES) {
        logError(
          `보안 취약점 ${totalVulnerabilities}개 발견 (허용: ${QUALITY_GATES.MAX_SECURITY_VULNERABILITIES}개)`
        )
        this.results.security.passed = false
        return false
      }

      logSuccess('보안 체크 통과!')
      this.results.security.passed = true
      return true
    } catch (error) {
      logWarning('보안 체크를 건너뜁니다 (npm audit 실패)')
      this.results.security.passed = true
      return true
    }
  }

  async checkFileSizes() {
    logSection('파일 크기 체크')

    try {
      // 간단한 파일 크기 체크 (실제로는 더 정교한 도구 사용 권장)
      const result = execSync('find src -name "*.ts" -o -name "*.svelte" | xargs wc -l | tail -1', {
        encoding: 'utf8',
        stdio: 'pipe'
      })

      const totalLines = parseInt(result.trim().split(' ')[0]) || 0
      this.results.fileSize.maxSize = totalLines

      if (totalLines > QUALITY_GATES.MAX_FILE_SIZE * 100) {
        // 전체 프로젝트 기준
        logWarning(
          `전체 코드 라인 수 ${totalLines}줄 (권장: ${QUALITY_GATES.MAX_FILE_SIZE * 100}줄 이하)`
        )
        this.results.fileSize.passed = false
        return false
      }

      logSuccess(`파일 크기 체크 통과! (${totalLines}줄)`)
      this.results.fileSize.passed = true
      return true
    } catch (error) {
      logWarning('파일 크기 체크를 건너뜁니다')
      this.results.fileSize.passed = true
      return true
    }
  }

  generateReport() {
    logSection('품질 게이트 리포트')

    const allPassed = Object.values(this.results).every(result =>
      typeof result === 'boolean' ? result : result.passed
    )

    this.results.overall.passed = allPassed

    // 결과 출력
    logInfo('TypeScript: ' + (this.results.typescript.passed ? '✅ 통과' : '❌ 실패'))
    logInfo(`  - 오류: ${this.results.typescript.errors}개`)
    logInfo(`  - 경고: ${this.results.typescript.warnings}개`)

    logInfo('ESLint: ' + (this.results.eslint.passed ? '✅ 통과' : '❌ 실패'))
    logInfo(`  - 오류: ${this.results.eslint.errors}개`)
    logInfo(`  - 경고: ${this.results.eslint.warnings}개`)

    logInfo('빌드: ' + (this.results.build.passed ? '✅ 통과' : '❌ 실패'))
    logInfo(`  - 시간: ${this.results.build.time.toFixed(2)}초`)

