  employeeId: string,
  agreementList: PolicyAgreement[]
): PolicyAgreement[] {
  return agreementList.filter(agreement => agreement.employeeId === employeeId)
}

export function hasEmployeeAgreedToPolicy(
  employeeId: string,
  policyId: string,
  agreementList: PolicyAgreement[]
): boolean {
  return agreementList.some(
    agreement => agreement.employeeId === employeeId && agreement.policyId === policyId
  )
}

export function searchFAQs(query: string, faqList: FAQ[]): FAQ[] {
  const lowercaseQuery = query.toLowerCase()
  return faqList.filter(
    faq =>
      faq.status === 'active' &&
      (faq.question.toLowerCase().includes(lowercaseQuery) ||
        faq.answer.toLowerCase().includes(lowercaseQuery) ||
        faq.tags.some(tag => tag.toLowerCase().includes(lowercaseQuery)))
  )
}

export function getPopularFAQs(faqList: FAQ[], limit: number = 5): FAQ[] {
  return faqList
    .filter(faq => faq.status === 'active')
    .sort((a, b) => b.viewCount - a.viewCount)
    .slice(0, limit)
}

import { writable } from 'svelte/store'
import type { AuditLog, Person } from './types'

// 감사 로그 관리
export const auditLogs = writable<AuditLog[]>([])
export const securityPolicies = writable<Record<string, any>>({})
export const accessControl = writable<Record<string, any>>({})

// 감사 로그 생성
export function createAuditLog(
  actorId: string,
  action: string,
  entity: string,
  entityId: string,
  oldData: any,
  newData: any,
  metadata?: Record<string, any>
): string {
  const auditLog: AuditLog = {
    id: crypto.randomUUID(),
    actorId,
    action,
    entity,
    entityId,
    diff: { old: oldData, new: newData },
    at: new Date().toISOString()
  }

  // 메타데이터 추가
  if (metadata) {
    auditLog.metadata = metadata
  }

  auditLogs.update(logs => [...logs, auditLog])

  return auditLog.id
}

// 감사 로그 조회
export function getAuditLogs(filters?: {
  actorId?: string
  entity?: string
  entityId?: string
  action?: string
  startDate?: string
  endDate?: string
}): AuditLog[] {
  let filteredLogs: AuditLog[] = []

  auditLogs.subscribe(logs => {
    filteredLogs = logs.filter(log => {
      if (filters?.actorId && log.actorId !== filters.actorId) return false
      if (filters?.entity && log.entity !== filters.entity) return false
      if (filters?.entityId && log.entityId !== filters.entityId) return false
      if (filters?.action && log.action !== filters.action) return false
      if (filters?.startDate && log.at < filters.startDate) return false
      if (filters?.endDate && log.at > filters.endDate) return false
      return true
    })
  })()

  return filteredLogs.sort((a, b) => new Date(b.at).getTime() - new Date(a.at).getTime())
}

// 엔티티별 감사 로그 조회
export function getEntityAuditTrail(entity: string, entityId: string): AuditLog[] {
  return getAuditLogs({ entity, entityId })
}

// 사용자별 감사 로그 조회
export function getUserAuditTrail(actorId: string): AuditLog[] {
  return getAuditLogs({ actorId })
}

