  return {
    recentLogs,
    violations: violations.violations,
    totalLogs: getAuditLogs().length,
    securityEvents: {
      login: getAuditLogs({ action: 'security_login' }).length,
      logout: getAuditLogs({ action: 'security_logout' }).length,
