// CRUD 함수들
export function addPerson(person: Omit<Person, 'id' | 'createdAt' | 'updatedAt'>): string {
  const id = crypto.randomUUID()
  const now = new Date().toISOString()
  const newPerson: Person = {
    ...person,
    id,
    createdAt: now,
    updatedAt: now
  }

  persons.update(list => [...list, newPerson])
  logAudit('create', 'person', id, {}, newPerson)
  return id
}

export function updatePerson(id: string, updates: Partial<Person>): void {
  persons.update(list => {
    const index = list.findIndex(p => p.id === id)
