}

// 마일스톤 승인 처리
export function approveMilestone(
  milestoneId: string,
  approverId: string,
  decision: 'approved' | 'rejected',
  comment?: string
): void {
  updateMilestoneStatus(milestoneId, decision === 'approved' ? 'completed' : 'not-started', comment)

  logAudit('approval_decision', 'milestone', milestoneId, { approverId, decision, comment }, {})
}

// 분기별 목표 설정 템플릿
export function createQuarterlyMilestoneTemplate(
  projectId: string,
  quarter: number,
  templateType: 'research' | 'development' | 'commercialization'
): Milestone[] {
  const templates = {
    research: [
      {
        title: '문헌 조사 및 기술 분석',
        kpis: { literature_review: 0, technical_analysis: 0 },
        deliverables: ['문헌조사보고서', '기술분석보고서']
      },
      {
        title: '실험 설계 및 초기 실험',
        kpis: { experiment_design: 0, initial_experiments: 0 },
        deliverables: ['실험설계서', '초기실험결과']
      },
      {
        title: '중간 결과 분석 및 보고',
        kpis: { data_analysis: 0, interim_report: 0 },
        deliverables: ['중간보고서', '데이터분석결과']
      }
    ],
    development: [
      {
        title: '요구사항 분석 및 설계',
        kpis: { requirements_analysis: 0, system_design: 0 },
        deliverables: ['요구사항명세서', '시스템설계서']
      },
      {
        title: '프로토타입 개발',
        kpis: { prototype_development: 0, testing: 0 },
        deliverables: ['프로토타입', '테스트결과']
      },
      {
        title: '성능 최적화 및 검증',
        kpis: { optimization: 0, validation: 0 },
        deliverables: ['최적화결과', '검증보고서']
      }
    ],
    commercialization: [
      {
        title: '시장 분석 및 사업화 계획',
        kpis: { market_analysis: 0, business_plan: 0 },
        deliverables: ['시장분석보고서', '사업화계획서']
      },
      {
        title: '파일럿 테스트 및 검증',
        kpis: { pilot_test: 0, validation: 0 },
        deliverables: ['파일럿테스트결과', '검증보고서']
      },
      {
        title: '사업화 준비 및 실행',
        kpis: { commercialization_prep: 0, execution: 0 },
        deliverables: ['사업화준비보고서', '실행계획서']
      }
    ]
  }

  const template = templates[templateType]
  const createdMilestones: Milestone[] = []

  template.forEach((item, index) => {
    const dueDate = new Date()
    dueDate.setMonth(dueDate.getMonth() + (index + 1) * 4) // 분기별로 4개월씩

    const milestoneId = createMilestone(
      projectId,
      quarter,
      item.title,
      item.kpis,
      dueDate.toISOString().split('T')[0],
      'current-user',
      item.deliverables
    )

    createdMilestones.push({
      id: milestoneId,
      projectId,
      quarter,
      title: item.title,
      kpis: item.kpis,
      dueDate: dueDate.toISOString().split('T')[0],
      ownerId: 'current-user',
      status: 'not-started',
      deliverables: item.deliverables,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    })
  })

  return createdMilestones
}

// 마일스톤 리포트 생성
export function generateMilestoneReport(
  projectId: string,
  quarter?: number
): {
  summary: any
  milestones: Milestone[]
  deliverables: Record<string, any[]>
  progress: any
} {
  const projectMilestones = quarter
    ? getMilestonesByQuarter(projectId, quarter)
    : getMilestonesByProject(projectId)

  const deliverables: Record<string, any[]> = {}
  const progress = calculateProjectProgress(projectId)

  projectMilestones.forEach(milestone => {
    deliverables[milestone.id] = getMilestoneDeliverables(milestone.id)
  })

  const summary = {
    totalMilestones: projectMilestones.length,
    completedMilestones: projectMilestones.filter(m => m.status === 'completed').length,
    delayedMilestones: getDelayedMilestones().filter(m => m.projectId === projectId).length,
    atRiskMilestones: getAtRiskMilestones().filter(m => m.projectId === projectId).length,
    overallProgress: progress.overallProgress
  }

  return { summary, milestones: projectMilestones, deliverables, progress }
}

import { writable } from 'svelte/store'
import type { ParticipationAssignment, SalaryHistory } from './types'
import { logAudit } from './core'

// 참여율 및 급여 변동 관리
export const participationAssignments = writable<ParticipationAssignment[]>([])
export const salaryHistory = writable<SalaryHistory[]>([])

// 월별 인건비 배분표
export const monthlySalaryAllocations = writable<Record<string, any[]>>({})

// 참여율 배정
export function assignParticipation(
  projectId: string,
  personId: string,
  dateFrom: string,
  dateTo: string,
  ratePct: number
): string {
  const assignment: ParticipationAssignment = {
    id: crypto.randomUUID(),
    projectId,
    personId,
    dateFrom,
    dateTo,
    ratePct,
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }

  participationAssignments.update(assignments => [...assignments, assignment])
  logAudit('create', 'participation_assignment', assignment.id, {}, assignment)

  // 월별 배분표 재계산
  recalculateMonthlyAllocations()

  return assignment.id
}

// 참여율 수정
export function updateParticipation(
  assignmentId: string,
  updates: Partial<ParticipationAssignment>
): void {
  participationAssignments.update(assignments => {
    const index = assignments.findIndex(a => a.id === assignmentId)
    if (index === -1) return assignments

    const oldAssignment = assignments[index]
    const updatedAssignment = {
      ...oldAssignment,
      ...updates,
      updatedAt: new Date().toISOString()
    }

    const newAssignments = [...assignments]
    newAssignments[index] = updatedAssignment

    logAudit('update', 'participation_assignment', assignmentId, oldAssignment, updatedAssignment)

    // 월별 배분표 재계산
    recalculateMonthlyAllocations()

    return newAssignments
  })
}

// 급여 변동 등록
export function addSalaryChange(
  personId: string,
  effectiveFrom: string,
  baseSalary: number,
  currency: string = 'KRW'
): string {
  const salaryRecord: SalaryHistory = {
    id: crypto.randomUUID(),
    personId,
    effectiveFrom,
    baseSalary,
    currency,
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }

  salaryHistory.update(history => [...history, salaryRecord])
  logAudit('create', 'salary_history', salaryRecord.id, {}, salaryRecord)

  // 월별 배분표 재계산
  recalculateMonthlyAllocations()

  return salaryRecord.id
}

// 월별 인건비 배분표 계산
export function recalculateMonthlyAllocations(): void {
  // 모든 프로젝트와 참여 배정을 가져와서 월별로 계산
  const allocations: Record<string, any[]> = {}

  participationAssignments.subscribe(assignments => {
    salaryHistory.subscribe(salaries => {
      projects.subscribe(projectList => {
        persons.subscribe(personList => {
          // 월별로 그룹화하여 계산
          const monthlyData: Record<string, any[]> = {}

          assignments.forEach(assignment => {
            const project = projectList.find(p => p.id === assignment.projectId)
            const person = personList.find(p => p.id === assignment.personId)

            if (!project || !person) return

            // 배정 기간의 각 월에 대해 계산
            const startDate = new Date(assignment.dateFrom)
            const endDate = new Date(assignment.dateTo)

            const currentDate = new Date(startDate.getFullYear(), startDate.getMonth(), 1)
            const endMonth = new Date(endDate.getFullYear(), endDate.getMonth(), 1)

            while (currentDate <= endMonth) {
              const monthKey = `${currentDate.getFullYear()}-${String(currentDate.getMonth() + 1).padStart(2, '0')}`

              if (!monthlyData[monthKey]) {
                monthlyData[monthKey] = []
              }

              // 해당 월의 급여 정보 가져오기
              const monthlySalary = getSalaryForMonth(person.id, currentDate, salaries)

              // 해당 월의 근무일 수 계산
              const workingDays = getWorkingDaysInMonth(currentDate, startDate, endDate)
              const totalDays = getDaysInMonth(currentDate)
              const workingRatio = workingDays / totalDays

              // 인건비 계산
              const monthlyAllocation = {
                projectId: project.id,
                projectCode: project.code,
                projectTitle: project.title,
                personId: person.id,
                personName: person.name,
                participationRate: assignment.ratePct,
                baseSalary: monthlySalary,
                workingRatio,
                allocatedAmount: monthlySalary * (assignment.ratePct / 100) * workingRatio,
                currency: 'KRW',
                month: monthKey
              }

              monthlyData[monthKey].push(monthlyAllocation)

              // 다음 달로 이동
              currentDate.setMonth(currentDate.getMonth() + 1)
            }
          })

          monthlySalaryAllocations.set(monthlyData)
        })()
      })()
    })()
  })()
}

// 특정 월의 급여 정보 가져오기
function getSalaryForMonth(personId: string, month: Date, salaries: SalaryHistory[]): number {
  // 해당 월에 유효한 급여 정보 찾기
  const validSalaries = salaries
    .filter(s => s.personId === personId)
    .filter(s => new Date(s.effectiveFrom) <= month)
    .sort((a, b) => new Date(b.effectiveFrom).getTime() - new Date(a.effectiveFrom).getTime())

  return validSalaries.length > 0 ? validSalaries[0].baseSalary : 0
}

// 특정 월의 근무일 수 계산
function getWorkingDaysInMonth(month: Date, startDate: Date, endDate: Date): number {
  const monthStart = new Date(month.getFullYear(), month.getMonth(), 1)
  const monthEnd = new Date(month.getFullYear(), month.getMonth() + 1, 0)

  const effectiveStart = startDate > monthStart ? startDate : monthStart
  const effectiveEnd = endDate < monthEnd ? endDate : monthEnd

  if (effectiveStart > effectiveEnd) return 0

  return Math.ceil((effectiveEnd.getTime() - effectiveStart.getTime()) / (1000 * 60 * 60 * 24)) + 1
}

// 특정 월의 총 일수
function getDaysInMonth(month: Date): number {
  return new Date(month.getFullYear(), month.getMonth() + 1, 0).getDate()
}

// 프로젝트별 월별 인건비 집계
export function getProjectMonthlyAllocations(projectId: string, month: string): any[] {
  let allocations: any[] = []

  monthlySalaryAllocations.subscribe(monthlyData => {
    allocations = monthlyData[month]?.filter(a => a.projectId === projectId) || []
  })()

  return allocations
}

// 프로젝트별 총 인건비 계산
export function getProjectTotalPersonnelCost(
  projectId: string,
  startMonth: string,
  endMonth: string
): number {
  let totalCost = 0

  monthlySalaryAllocations.subscribe(monthlyData => {
    const startDate = new Date(startMonth + '-01')
    const endDate = new Date(endMonth + '-01')

    const currentDate = new Date(startDate)
    while (currentDate <= endDate) {
      const monthKey = `${currentDate.getFullYear()}-${String(currentDate.getMonth() + 1).padStart(2, '0')}`
      const monthAllocations = monthlyData[monthKey] || []

      const projectAllocations = monthAllocations.filter(a => a.projectId === projectId)
      const monthTotal = projectAllocations.reduce((sum, a) => sum + a.allocatedAmount, 0)

      totalCost += monthTotal

      currentDate.setMonth(currentDate.getMonth() + 1)
    }
  })()

  return totalCost
}

// 개인별 월별 참여 현황
export function getPersonMonthlyParticipation(personId: string, month: string): any[] {
  let participations: any[] = []

  monthlySalaryAllocations.subscribe(monthlyData => {
    participations = monthlyData[month]?.filter(a => a.personId === personId) || []
  })()

  return participations
}

// 개인별 총 참여율 계산 (특정 월)
export function getPersonTotalParticipationRate(personId: string, month: string): number {
  const participations = getPersonMonthlyParticipation(personId, month)
  return participations.reduce((sum, p) => sum + p.participationRate, 0)
}

// 참여율 초과 경고 체크
export function checkParticipationOverload(
  personId: string,
  month: string
): {
  overloaded: boolean
  totalRate: number
  threshold: number
} {
  const totalRate = getPersonTotalParticipationRate(personId, month)
  const threshold = 100 // 100% 초과 시 경고

  return {
    overloaded: totalRate > threshold,
    totalRate,
    threshold
  }
}

