// Derived stores
export const activeProjects = derived(projectStore, $store =>
  $store.projects.filter(p => p.status === 'active')
)

export const projectStatusStats = derived(projectStore, $store => {
  const stats: ProjectStatusStats = {
    planning: 0,
    active: 0,
    completed: 0,
    cancelled: 0,
    suspended: 0
  }

  $store.projects.forEach(project => {
    stats[project.status] = (stats[project.status] || 0) + 1
  })

  return stats
})

export const overParticipationEmployees = derived(projectStore, $store =>
  $store.employeeParticipationSummary.filter(emp => emp.participationStatus === 'OVER_LIMIT')
)

export const participationRateStats = derived(projectStore, $store => {
  const summary = $store.employeeParticipationSummary
  const stats: ParticipationRateStats = {
    overLimit: summary.filter(emp => emp.participationStatus === 'OVER_LIMIT').length,
    full: summary.filter(emp => emp.participationStatus === 'FULL').length,
    available: summary.filter(emp => emp.participationStatus === 'AVAILABLE').length,
    averageRate:
      summary.length > 0
        ? summary.reduce((sum, emp) => sum + emp.totalParticipationRate, 0) / summary.length
        : 0
  }

  return stats
})

// 초기 데이터 로드
export const initializeProjectManagement = async () => {
  await Promise.all([
    projectActions.loadProjectSummary(),
    participationRateActions.loadEmployeeParticipationSummary(),
    budgetActions.loadBudgetSummaryByYear(),
    alertActions.loadAlerts()
  ])
}

import { writable } from 'svelte/store'
import type { Person } from './types'

// RBAC 권한 정의
export const ROLES = {
  R1: '연구원',
  R2: 'PM(과제책임자)',
  R3: '담당부서(구매·기술 등)',
  R4: '경영지원(회계·총무)',
  R5: '연구소장',
  R6: '경영진',
  R7: '감사/외부평가'
} as const

export type Role = keyof typeof ROLES

// 권한 매트릭스
export const PERMISSIONS = {
  // Project 권한
  PROJECT_READ: ['R1', 'R2', 'R3', 'R4', 'R5', 'R6', 'R7'],
  PROJECT_CREATE: ['R4'],
  PROJECT_UPDATE: ['R2', 'R4'],
  PROJECT_APPROVE: ['R6'],
  PROJECT_LOCK: ['R7'],

  // ExpenseItem 권한
  EXPENSE_READ: ['R1', 'R2', 'R3', 'R4', 'R5', 'R6', 'R7'],
  EXPENSE_CREATE: ['R1', 'R2'],
  EXPENSE_UPDATE: ['R3', 'R4'],
  EXPENSE_APPROVE: ['R2', 'R4', 'R5'],
  EXPENSE_LOCK: ['R4'],

  // Document 권한
  DOCUMENT_READ: ['R1', 'R2', 'R3', 'R4', 'R5', 'R6', 'R7'],
  DOCUMENT_CREATE: ['R1', 'R2', 'R3'],
  DOCUMENT_UPDATE: ['R4'],
  DOCUMENT_LOCK: ['R4'],

  // ResearchNote 권한
  RESEARCH_NOTE_READ: ['R1', 'R2', 'R5'],
  RESEARCH_NOTE_CREATE: ['R1'],
  RESEARCH_NOTE_UPDATE: ['R1'],
  RESEARCH_NOTE_APPROVE: ['R2'],

  // SubmissionBundle 권한
  BUNDLE_READ: ['R4', 'R6', 'R7'],
  BUNDLE_CREATE: ['R4'],
  BUNDLE_APPROVE: ['R4'],
  BUNDLE_LOCK: ['R7']
} as const

export type Permission = keyof typeof PERMISSIONS

// 현재 사용자 정보
export const currentUser = writable<Person | null>(null)

// 권한 체크 함수들
export function hasRole(user: Person | null, role: Role): boolean {
  if (!user) return false
  return user.roleSet.includes(role)
}

export function hasPermission(user: Person | null, permission: Permission): boolean {
  if (!user) return false
  const allowedRoles = PERMISSIONS[permission]
  return user.roleSet.some(role => allowedRoles.includes(role as Role))
}

export function hasAnyRole(user: Person | null, roles: Role[]): boolean {
  if (!user) return false
  return roles.some(role => user.roleSet.includes(role))
}

export function hasAllRoles(user: Person | null, roles: Role[]): boolean {
  if (!user) return false
  return roles.every(role => user.roleSet.includes(role))
}

// 역할별 권한 체크 함수들
export function canReadProject(user: Person | null): boolean {
