// 대체 추천 대시보드 데이터
export function getReplacementDashboardData(): any {
  let allRecommendations: ReplacementRecommendation[] = []

  replacementRecommendations.subscribe(list => {
    allRecommendations = list
  })()

  const recentRecommendations = allRecommendations
    .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
    .slice(0, 10)

  const pendingCount = allRecommendations.filter(r => r.status === 'pending').length
  const approvedCount = allRecommendations.filter(r => r.status === 'approved').length
  const rejectedCount = allRecommendations.filter(r => r.status === 'rejected').length

  return {
    recentRecommendations,
    pendingCount,
    approvedCount,
    rejectedCount,
    totalCount: allRecommendations.length
  }
}

import { writable } from 'svelte/store'
import type {
  Report
} from './types'
import { logAudit } from './core'

// 리포트 관리
export const reports = writable<Report[]>([])
export const reportTemplates = writable<Record<string, any>>({})

// 주간 리포트 생성
export function generateWeeklyReport(
  projectId: string,
  weekStart: string,
  weekEnd: string
): string {
  const reportId = crypto.randomUUID()

  // 데이터 수집
  const summaryData = collectWeeklyData(projectId, weekStart, weekEnd)

  const report: Report = {
    id: reportId,
    projectId,
    type: 'weekly',
    periodStart: weekStart,
    periodEnd: weekEnd,
    summaryJson: summaryData,
    generatedAt: new Date().toISOString(),
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }

  reports.update(list => [...list, report])
  logAudit('generate', 'weekly_report', reportId, {}, report)

  return reportId
}

// 주간 데이터 수집
function collectWeeklyData(projectId: string, weekStart: string, weekEnd: string): any {
  // 마일스톤 달성률
  const milestoneProgress = calculateMilestoneProgress(projectId, weekStart, weekEnd)

  // 예산 집행률
  const budgetExecution = calculateBudgetExecution(projectId, weekStart, weekEnd)

  // 인력 참여율
  const participationRate = calculateParticipationRate(projectId, weekStart, weekEnd)

  // 연구노트 제출률
  const researchNoteSubmission = calculateResearchNoteSubmission(projectId, weekStart, weekEnd)

  // 이슈 및 리스크
  const issuesAndRisks = collectIssuesAndRisks(projectId, weekStart, weekEnd)

  // 다음 주 계획
  const nextWeekPlan = generateNextWeekPlan(projectId)

  return {
    milestoneProgress,
    budgetExecution,
    participationRate,
    researchNoteSubmission,
    issuesAndRisks,
    nextWeekPlan,
    generatedAt: new Date().toISOString()
  }
}

