// 리포트 통계
export function getReportStatistics(projectId: string): any {
  let projectReports: Report[] = []

  reports.subscribe(list => {
    projectReports = list.filter(r => r.projectId === projectId)
  })()

  const weeklyReports = projectReports.filter(r => r.type === 'weekly')
  const quarterlyReports = projectReports.filter(r => r.type === 'quarterly')

  return {
    totalReports: projectReports.length,
    weeklyReports: weeklyReports.length,
    quarterlyReports: quarterlyReports.length,
    lastReportDate:
      projectReports.length > 0
        ? Math.max(...projectReports.map(r => new Date(r.generatedAt).getTime()))
        : null,
    averageGenerationTime: calculateAverageGenerationTime(projectReports)
  }
}

// 평균 생성 시간 계산
function calculateAverageGenerationTime(reports: Report[]): number {
  if (reports.length === 0) return 0

  const totalTime = reports.reduce((sum, report) => {
    const created = new Date(report.createdAt).getTime()
    const generated = new Date(report.generatedAt).getTime()
    return sum + (generated - created)
  }, 0)

  return totalTime / reports.length
}

import { writable } from 'svelte/store'
import type { ResearchNote, Document } from './types'
import { logAudit } from './core'

// 연구노트 관리
export const researchNotes = writable<ResearchNote[]>([])
export const researchNoteAttachments = writable<Record<string, Document[]>>({})
export const researchNoteSignatures = writable<Record<string, any[]>>({})

// 연구노트 생성
export function createResearchNote(
  projectId: string,
  authorId: string,
  weekOf: string,
  title: string,
  contentMd: string,
  attachments: string[] = []
): string {
  const note: ResearchNote = {
    id: crypto.randomUUID(),
    projectId,
    authorId,
    weekOf,
    title,
    contentMd,
    attachments,
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }

  researchNotes.update(list => [...list, note])
  logAudit('create', 'research_note', note.id, {}, note)

  return note.id
}

// 연구노트 수정
export function updateResearchNote(noteId: string, updates: Partial<ResearchNote>): void {
  researchNotes.update(list => {
    const index = list.findIndex(n => n.id === noteId)
    if (index === -1) return list

    const oldNote = list[index]
    const updatedNote = {
      ...oldNote,
      ...updates,
      updatedAt: new Date().toISOString()
    }

    const newList = [...list]
    newList[index] = updatedNote

    logAudit('update', 'research_note', noteId, oldNote, updatedNote)
    return newList
  })
}

// 연구노트 삭제
export function deleteResearchNote(noteId: string): void {
  researchNotes.update(list => {
    const note = list.find(n => n.id === noteId)
    if (note) {
      logAudit('delete', 'research_note', noteId, note, {})
    }
    return list.filter(n => n.id !== noteId)
  })
}

// 첨부파일 추가
export function addResearchNoteAttachment(
  noteId: string,
  filename: string,
  storageUrl: string,
  sha256: string,
  description?: string
): string {
  const attachment: Document = {
    id: crypto.randomUUID(),
    projectId: '', // 연구노트의 프로젝트 ID로 설정
    type: 'other',
    filename,
    storageUrl,
    sha256,
    version: 1,
    meta: { description, noteId },
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  }

  researchNoteAttachments.update(attachments => {
    const noteAttachments = attachments[noteId] || []
    return {
      ...attachments,
      [noteId]: [...noteAttachments, attachment]
    }
  })

  logAudit('add_attachment', 'research_note', noteId, {}, attachment)

  return attachment.id
}

// 전자서명 추가
export function addResearchNoteSignature(
  noteId: string,
  signerId: string,
  signatureData: string,
  signatureType: 'author' | 'verifier' = 'author'
): string {
  const signature = {
    id: crypto.randomUUID(),
    noteId,
    signerId,
    signatureData,
    signatureType,
    signedAt: new Date().toISOString(),
    verified: false
  }

  researchNoteSignatures.update(signatures => {
    const noteSignatures = signatures[noteId] || []
    return {
      ...signatures,
      [noteId]: [...noteSignatures, signature]
    }
  })

  logAudit('add_signature', 'research_note', noteId, { signerId, signatureType }, signature)

  // 연구노트 서명 상태 업데이트
  updateResearchNote(noteId, { signedAt: signature.signedAt })

  return signature.id
}

// 서명 검증
export function verifyResearchNoteSignature(
  noteId: string,
  signatureId: string,
  verifierId: string
): void {
  researchNoteSignatures.update(signatures => {
    const noteSignatures = signatures[noteId] || []
    const updatedSignatures = noteSignatures.map(sig => {
      if (sig.id === signatureId) {
        return {
          ...sig,
          verified: true,
          verifiedBy: verifierId,
          verifiedAt: new Date().toISOString()
        }
      }
      return sig
    })

    return {
      ...signatures,
      [noteId]: updatedSignatures
    }
  })

  logAudit('verify_signature', 'research_note', noteId, { signatureId, verifierId }, {})

  // 연구노트 검증 상태 업데이트
  updateResearchNote(noteId, { verifiedBy: verifierId })
}

// 프로젝트별 연구노트 목록
