// 급여 계약 생성
export async function createContract(contractData: CreateSalaryContractRequest): Promise<boolean> {
  isLoading.set(true)
  error.set(null)

  try {
    const response = await fetch('/api/salary/contracts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(contractData)
    })

    const result: ApiResponse<SalaryContract> = await response.json()

    if (result.success && result.data) {
      // 새 계약을 목록에 추가
      contracts.update(current => [result.data!, ...current])
      return true
    } else {
      error.set(result.error || '급여 계약 생성에 실패했습니다.')
      return false
    }
  } catch (err) {
    error.set(err instanceof Error ? err.message : '알 수 없는 오류가 발생했습니다.')
    return false
  } finally {
    isLoading.set(false)
  }
}

// 급여 계약 수정
export async function updateContract(
  contractId: string,
  updateData: UpdateSalaryContractRequest
): Promise<boolean> {
  error.set(null)

  try {
    const response = await fetch(`/api/salary/contracts/${contractId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updateData)
    })

    const result: ApiResponse<SalaryContract> = await response.json()

    if (result.success && result.data) {
      // 전체 목록을 다시 로드하여 완전한 데이터로 업데이트
      // loadContracts는 자체적으로 isLoading을 관리하므로 여기서는 설정하지 않음
      await loadContracts()
      return true
    } else {
      error.set(result.error || '급여 계약 수정에 실패했습니다.')
      return false
    }
  } catch (err) {
    error.set(err instanceof Error ? err.message : '알 수 없는 오류가 발생했습니다.')
    return false
  }
}

// 급여 계약 삭제
export async function deleteContract(contractId: string): Promise<boolean> {
  isLoading.set(true)
  error.set(null)

  try {
    const response = await fetch(`/api/salary/contracts/${contractId}`, {
      method: 'DELETE'
    })

    const result: ApiResponse<{ id: string }> = await response.json()

    if (result.success) {
      // 목록에서 해당 계약 제거
      contracts.update(current => current.filter(contract => contract.id !== contractId))
      return true
    } else {
      error.set(result.error || '급여 계약 삭제에 실패했습니다.')
      return false
    }
  } catch (err) {
    error.set(err instanceof Error ? err.message : '알 수 없는 오류가 발생했습니다.')
    return false
  } finally {
    isLoading.set(false)
