// 파일 변경 감지 및 자동 검증
export class AutoValidationHooks {
  private static readonly WATCH_DIRS = ['src/lib/utils', 'src/routes/api/project-management']
  private static readonly SUPPORTED_EXTENSIONS = ['.ts', '.js', '.svelte']
  private static watchers: Map<string, any> = new Map()
  private static isEnabled = false

  /**
   * 자동 검증 시스템 활성화
   */
  static enable(): void {
    if (this.isEnabled) {
      console.log('⚠️ [자동 검증] 이미 활성화되어 있습니다.')
      return
    }

    console.log('🚀 [자동 검증] 시스템 활성화 시작')

    // 파일 변경 감지 시작
    this.startFileWatching()

    // Git 훅 설정
    this.setupGitHooks()

    // IDE 확장 프로그램 연동
    this.setupIDEIntegration()

    this.isEnabled = true
    console.log('✅ [자동 검증] 시스템 활성화 완료')
  }

  /**
   * 자동 검증 시스템 비활성화
   */
  static disable(): void {
    if (!this.isEnabled) {
      console.log('⚠️ [자동 검증] 이미 비활성화되어 있습니다.')
      return
    }

    console.log('🛑 [자동 검증] 시스템 비활성화 시작')

    // 파일 감지 중지
    this.stopFileWatching()

    this.isEnabled = false
    console.log('✅ [자동 검증] 시스템 비활성화 완료')
  }

  /**
   * 파일 변경 감지 시작
   */
  private static startFileWatching(): void {
    for (const dir of this.WATCH_DIRS) {
      try {
        const watcher = watch(dir, { recursive: true }, (eventType, filename) => {
          if (filename && this.SUPPORTED_EXTENSIONS.includes(extname(filename))) {
            const filePath = join(dir, filename)
            this.handleFileChange(eventType, filePath)
          }
        })

        this.watchers.set(dir, watcher)
        console.log(`👀 [파일 감지] ${dir} 감시 시작`)
      } catch (error) {
        console.error(`❌ [파일 감지] ${dir} 감시 실패:`, error)
      }
    }
  }

  /**
   * 파일 변경 감지 중지
   */
  private static stopFileWatching(): void {
    for (const [dir, watcher] of this.watchers) {
      watcher.close()
      console.log(`👀 [파일 감지] ${dir} 감시 중지`)
    }
    this.watchers.clear()
  }

  /**
   * 파일 변경 처리
   */
  private static async handleFileChange(eventType: string, filePath: string): Promise<void> {
    console.log(`📝 [파일 변경] ${eventType}: ${filePath}`)

    try {
      // 1. 파일 내용 읽기
      const content = readFileSync(filePath, 'utf-8')

      // 2. 자동 검증 실행
      const validation = await this.runAutoValidation(filePath, content)

      // 3. 검증 결과 처리
      await this.handleValidationResult(filePath, validation)
    } catch (error) {
      console.error(`❌ [파일 변경 처리] ${filePath} 오류:`, error)
    }
  }

  /**
   * 자동 검증 실행
   */
  private static async runAutoValidation(
    filePath: string,
    content: string
  ): Promise<{
    coding: any
    schema: any
    dependency: any
  }> {
    const results = {
      coding: null as any,
      schema: null as any,
      dependency: null as any
    }

    try {
      // 1. 코딩 가이드라인 검증
      results.coding = AICodingValidator.validateCode(content, 'typescript')

      // 2. 스키마 검증 (API 파일인 경우)
      if (filePath.includes('/api/')) {
        results.schema = await SchemaValidator.validateDatabaseSchema()
      }

      // 3. 의존성 분석
      results.dependency = await this.analyzeDependencies(filePath)
    } catch (error) {
      console.error(`❌ [자동 검증] ${filePath} 오류:`, error)
    }

    return results
  }

  /**
   * 검증 결과 처리
   */
  private static async handleValidationResult(filePath: string, validation: any): Promise<void> {
    const hasErrors = validation.coding?.errors?.length > 0
    const hasWarnings = validation.coding?.warnings?.length > 0

    if (hasErrors) {
      console.log(`❌ [검증 실패] ${filePath}`)
      validation.coding.errors.forEach((error: string) => {
        console.log(`  - ${error}`)
      })

      // 자동 수정 시도
      await this.attemptAutoFix(filePath, validation)
    } else if (hasWarnings) {
      console.log(`⚠️ [검증 경고] ${filePath}`)
      validation.coding.warnings.forEach((warning: string) => {
        console.log(`  - ${warning}`)
      })
    } else {
      console.log(`✅ [검증 통과] ${filePath}`)
    }
  }

  /**
   * 자동 수정 시도
   */
  private static async attemptAutoFix(filePath: string, validation: any): Promise<void> {
    console.log(`🔧 [자동 수정] ${filePath} 시도`)

    try {
      // 변경 계획 생성
      const plan = await SafeChangeManager.createChangePlan(filePath, 'modify', '자동 수정')

      // 자동 수정 로직 실행
      await this.executeAutoFix(plan, validation)
    } catch (error) {
      console.error(`❌ [자동 수정] ${filePath} 실패:`, error)
    }
  }

  /**
   * 자동 수정 실행
   */
  private static async executeAutoFix(plan: any, validation: any): Promise<void> {
    // 실제 자동 수정 로직은 여기에 구현
    // 예: 코드 포맷팅, 타입 수정, import 정리 등
    console.log(`🔧 [자동 수정] 계획 ID: ${plan.id}`)
  }

  /**
   * 의존성 분석
   */
  private static async analyzeDependencies(filePath: string): Promise<any> {
    // 간단한 의존성 분석
    return {
      riskLevel: 'low',
      dependencies: [],
      dependents: []
    }
  }

  /**
   * Git 훅 설정
   */
  private static setupGitHooks(): void {
    console.log('🔗 [Git 훅] 설정 시작')

    // pre-commit 훅 설정
    const preCommitHook = `#!/bin/sh
# 자동 검증 실행
echo "🔍 [Git 훅] 커밋 전 검증 시작"
node -e "
const { AutoValidationHooks } = require('./src/lib/utils/auto-validation-hooks.ts');
AutoValidationHooks.runPreCommitValidation();
"
`

    // post-commit 훅 설정
    const postCommitHook = `#!/bin/sh
# 커밋 후 검증
echo "✅ [Git 훅] 커밋 후 검증 완료"
`

    try {
      writeFileSync('.git/hooks/pre-commit', preCommitHook)
      writeFileSync('.git/hooks/post-commit', postCommitHook)
      console.log('✅ [Git 훅] 설정 완료')
    } catch (error) {
      console.error('❌ [Git 훅] 설정 실패:', error)
    }
  }

  /**
   * IDE 확장 프로그램 연동
   */
  private static setupIDEIntegration(): void {
    console.log('🔌 [IDE 연동] 설정 시작')

    // VS Code 설정
    const vscodeSettings = {
      'files.watcherExclude': {
        '**/node_modules/**': true,
        '**/.git/**': true
      },
      'typescript.preferences.includePackageJsonAutoImports': 'auto',
      'editor.codeActionsOnSave': {
        'source.fixAll': true,
        'source.organizeImports': true
      }
    }

    try {
      writeFileSync('.vscode/settings.json', JSON.stringify(vscodeSettings, null, 2))
      console.log('✅ [IDE 연동] VS Code 설정 완료')
    } catch (error) {
      console.error('❌ [IDE 연동] 설정 실패:', error)
    }
  }

  /**
   * 커밋 전 검증 실행
   */
  static async runPreCommitValidation(): Promise<boolean> {
    console.log('🔍 [커밋 전 검증] 시작')

    try {
      // 변경된 파일들 검증
      const changedFiles = await this.getChangedFiles()
      let allValid = true

      for (const file of changedFiles) {
        const content = readFileSync(file, 'utf-8')
        const validation = await this.runAutoValidation(file, content)

        if (validation.coding?.errors?.length > 0) {
          console.log(`❌ [커밋 전 검증] ${file} 실패`)
          allValid = false
        }
      }

      if (allValid) {
        console.log('✅ [커밋 전 검증] 모든 파일 통과')
      } else {
        console.log('❌ [커밋 전 검증] 일부 파일 실패 - 커밋 중단')
      }

      return allValid
    } catch (error) {
      console.error('❌ [커밋 전 검증] 오류:', error)
      return false
    }
  }

  /**
   * 변경된 파일 목록 가져오기
   */
  private static async getChangedFiles(): Promise<string[]> {
    // Git 명령어로 변경된 파일 목록 가져오기
    // 실제 구현에서는 child_process를 사용
    return []
  }

  /**
   * 상태 조회
   */
  static getStatus(): {
    isEnabled: boolean
    watchedDirs: string[]
    watchersCount: number
  } {
    return {
      isEnabled: this.isEnabled,
      watchedDirs: this.WATCH_DIRS,
      watchersCount: this.watchers.size
    }
  }
}

import type { BankAccount, Transaction, ExpectedTransaction } from '$lib/stores/funds'
import type { BudgetCategory, BudgetGoal } from '$lib/stores/budget'
import type { Notification, NotificationSettings } from '$lib/stores/notifications'

export interface BackupData {
  version: string
  timestamp: string
  data: {
    bankAccounts: BankAccount[]
    transactions: Transaction[]
    expectedTransactions: ExpectedTransaction[]
    budgetCategories: BudgetCategory[]
    budgetGoals: BudgetGoal[]
    notifications: Notification[]
    notificationSettings: NotificationSettings
  }
}

// 데이터 백업
export function createBackup(
  bankAccounts: BankAccount[],
  transactions: Transaction[],
  expectedTransactions: ExpectedTransaction[],
  budgetCategories: BudgetCategory[],
  budgetGoals: BudgetGoal[],
  notifications: Notification[],
  notificationSettings: NotificationSettings
): BackupData {
  return {
    version: '1.0.0',
    timestamp: new Date().toISOString(),
    data: {
      bankAccounts,
      transactions,
      expectedTransactions,
      budgetCategories,
      budgetGoals,
      notifications,
      notificationSettings
    }
  }
}

// 백업 파일 다운로드
export function downloadBackup(backupData: BackupData) {
  const jsonString = JSON.stringify(backupData, null, 2)
  const blob = new Blob([jsonString], { type: 'application/json' })
  const url = URL.createObjectURL(blob)
  const a = document.createElement('a')
  a.href = url
  a.download = `workstream-backup-${new Date().toISOString().split('T')[0]}.json`
  document.body.appendChild(a)
  a.click()
  document.body.removeChild(a)
  URL.revokeObjectURL(url)
}

// 백업 파일 검증
export function validateBackup(data: any): data is BackupData {
  if (!data || typeof data !== 'object') {
    return false
  }

  if (!data.version || !data.timestamp || !data.data) {
    return false
  }

  const requiredFields = [
    'bankAccounts',
    'transactions',
    'expectedTransactions',
    'budgetCategories',
    'budgetGoals',
    'notifications',
    'notificationSettings'
  ]

  return requiredFields.every(
    field => Array.isArray(data.data[field]) || typeof data.data[field] === 'object'
  )
}

// 로컬 스토리지에 자동 백업
export function autoBackup(
  bankAccounts: BankAccount[],
  transactions: Transaction[],
  expectedTransactions: ExpectedTransaction[],
  budgetCategories: BudgetCategory[],
  budgetGoals: BudgetGoal[],
  notifications: Notification[],
  notificationSettings: NotificationSettings
) {
  const backupData = createBackup(
    bankAccounts,
    transactions,
    expectedTransactions,
    budgetCategories,
    budgetGoals,
    notifications,
    notificationSettings
  )

  try {
    localStorage.setItem('workstream-auto-backup', JSON.stringify(backupData))
    localStorage.setItem('workstream-backup-timestamp', backupData.timestamp)
  } catch (error) {
    console.error('자동 백업 실패:', error)
  }
}

// 로컬 스토리지에서 자동 백업 복원
export function loadAutoBackup(): BackupData | null {
  try {
    const backupString = localStorage.getItem('workstream-auto-backup')
    if (!backupString) {
      return null
    }

    const backupData = JSON.parse(backupString)
    if (validateBackup(backupData)) {
      return backupData
    }
  } catch (error) {
    console.error('자동 백업 로드 실패:', error)
  }

  return null
}

// 백업 파일 읽기
export function readBackupFile(file: File): Promise<BackupData> {
  return new Promise((resolve, reject) => {
    const reader = new FileReader()

    reader.onload = e => {
      try {
        const result = e.target?.result
        if (typeof result !== 'string') {
          throw new Error('파일 읽기 실패')
        }

        const backupData = JSON.parse(result)
        if (validateBackup(backupData)) {
          resolve(backupData)
        } else {
          reject(new Error('잘못된 백업 파일 형식입니다.'))
        }
      } catch (error) {
        reject(new Error('백업 파일 파싱 실패: ' + (error as Error).message))
      }
    }

    reader.onerror = () => {
      reject(new Error('파일 읽기 중 오류가 발생했습니다.'))
    }

    reader.readAsText(file)
  })
}

// 백업 복원 확인 메시지
export function getRestoreConfirmMessage(backupData: BackupData): string {
  const backupDate = new Date(backupData.timestamp).toLocaleString('ko-KR')
  const dataCount = {
    accounts: backupData.data.bankAccounts.length,
    transactions: backupData.data.transactions.length,
    expectedTransactions: backupData.data.expectedTransactions.length,
    budgetCategories: backupData.data.budgetCategories.length,
    budgetGoals: backupData.data.budgetGoals.length
  }

  return `
다음 백업을 복원하시겠습니까?

백업 일시: ${backupDate}
버전: ${backupData.version}

포함된 데이터:
- 계좌: ${dataCount.accounts}개
- 거래내역: ${dataCount.transactions}개  
- 예상거래: ${dataCount.expectedTransactions}개
- 예산카테고리: ${dataCount.budgetCategories}개
- 예산목표: ${dataCount.budgetGoals}개

※ 현재 데이터는 모두 대체됩니다.
	`.trim()
}

// 백업 통계
export interface BackupStats {
  lastBackupTime: string | null
  backupSize: number
  totalRecords: number
}

export function getBackupStats(
  bankAccounts: BankAccount[],
  transactions: Transaction[],
  expectedTransactions: ExpectedTransaction[],
  budgetCategories: BudgetCategory[],
  budgetGoals: BudgetGoal[]
): BackupStats {
  const lastBackupTime = localStorage.getItem('workstream-backup-timestamp')
  const totalRecords =
    bankAccounts.length +
    transactions.length +
    expectedTransactions.length +
    budgetCategories.length +
    budgetGoals.length

  const backupData = createBackup(
    bankAccounts,
    transactions,
    expectedTransactions,
    budgetCategories,
    budgetGoals,
    [],
    {} as NotificationSettings
  )
  const backupSize = JSON.stringify(backupData).length

  return {
    lastBackupTime,
    backupSize,
    totalRecords
  }
}

/**
 * 날짜 계산 전용 유틸리티 함수들
 * 모든 날짜 관련 계산을 중앙화하여 일관성 보장
 * UTC+9 (Asia/Seoul) 타임존 적용
 */

import { getCurrentUTC, toUTC } from './date-handler'

/**
 * 프로젝트 연차별 기간 계산 (UTC+9 타임존 적용)
 * @param projectStartDate 프로젝트 시작일
 * @param periodNumber 연차 번호 (1, 2, 3...)
 * @returns 해당 연차의 시작일과 종료일
 */
export function calculateAnnualPeriod(
  projectStartDate: string | Date,
  periodNumber: number
): { startDate: string; endDate: string } {
  // UTC+9 타임존으로 입력 날짜 변환
  const startUtc = toUTC(projectStartDate)
  const start = new Date(startUtc)

  // 연차별 시작일 계산 (각 연차는 12개월)
  const periodStartDate = new Date(start)
  periodStartDate.setUTCFullYear(start.getUTCFullYear() + (periodNumber - 1))

  // 연차별 종료일 계산 (12개월 후의 전날)
  const periodEndDate = new Date(periodStartDate)
  periodEndDate.setUTCMonth(periodEndDate.getUTCMonth() + 12)
  periodEndDate.setUTCDate(periodEndDate.getUTCDate() - 1)

  return {
    startDate: formatDateForAPI(periodStartDate),
    endDate: formatDateForAPI(periodEndDate)
  }
}

/**
 * 참여연구원 참여기간 검증 및 계산 (UTC+9 타임존 적용)
 * @param participationStartDate 참여 시작일
 * @param participationEndDate 참여 종료일
 * @param projectStartDate 프로젝트 시작일
 * @param projectEndDate 프로젝트 종료일
 * @returns 검증 결과와 정규화된 날짜
 */
export function calculateParticipationPeriod(
  participationStartDate: string | Date,
  participationEndDate: string | Date,
  projectStartDate: string | Date,
  projectEndDate: string | Date
): {
  isValid: boolean
  normalizedStartDate: string
  normalizedEndDate: string
  errorMessage?: string
} {
  // UTC+9 타임존으로 모든 날짜 변환
  const partStartUtc = toUTC(participationStartDate)
  const partEndUtc = toUTC(participationEndDate)
  const projStartUtc = toUTC(projectStartDate)
  const projEndUtc = toUTC(projectEndDate)

  const partStart = new Date(partStartUtc)
  const partEnd = new Date(partEndUtc)
  const projStart = new Date(projStartUtc)
  const projEnd = new Date(projEndUtc)

  // 참여 시작일이 프로젝트 종료일보다 늦으면 유효하지 않음
  if (partStart > projEnd) {
    return {
      isValid: false,
      normalizedStartDate: '',
      normalizedEndDate: '',
      errorMessage: '참여 시작일이 프로젝트 종료일보다 늦습니다.'
    }
  }

  // 참여 종료일이 프로젝트 시작일보다 이르면 유효하지 않음
  if (partEnd < projStart) {
    return {
      isValid: false,
      normalizedStartDate: '',
      normalizedEndDate: '',
      errorMessage: '참여 종료일이 프로젝트 시작일보다 이릅니다.'
    }
  }

  // 참여 시작일을 프로젝트 범위 내로 정규화
  const normalizedStartDate = partStart < projStart ? projStart : partStart

  // 참여 종료일을 프로젝트 범위 내로 정규화
  const normalizedEndDate = partEnd > projEnd ? projEnd : partEnd

  return {
    isValid: true,
    normalizedStartDate: formatDateForAPI(normalizedStartDate),
    normalizedEndDate: formatDateForAPI(normalizedEndDate)
  }
}

/**
 * 계약 기간과 프로젝트 기간의 겹침 검증
 * @param contractStartDate 계약 시작일
 * @param contractEndDate 계약 종료일
 * @param participationStartDate 참여 시작일
 * @param participationEndDate 참여 종료일
 * @returns 겹침 검증 결과
 */
export function validateContractOverlap(
  contractStartDate: string | Date,
  contractEndDate: string | Date,
  participationStartDate: string | Date,
  participationEndDate: string | Date
): {
  hasOverlap: boolean
  overlapStartDate?: string
  overlapEndDate?: string
  errorMessage?: string
} {
  const contractStart = new Date(contractStartDate)
  const contractEnd = new Date(contractEndDate)
  const partStart = new Date(participationStartDate)
  const partEnd = new Date(participationEndDate)

  // 계약 종료일이 참여 시작일보다 이르면 겹치지 않음
  if (contractEnd < partStart) {
    return {
      hasOverlap: false,
      errorMessage: '계약 종료일이 참여 시작일보다 이릅니다.'
    }
  }

  // 계약 시작일이 참여 종료일보다 늦으면 겹치지 않음
  if (contractStart > partEnd) {
    return {
      hasOverlap: false,
      errorMessage: '계약 시작일이 참여 종료일보다 늦습니다.'
    }
  }

  // 겹치는 기간 계산
  const overlapStart = contractStart > partStart ? contractStart : partStart
  const overlapEnd = contractEnd < partEnd ? contractEnd : partEnd

  return {
    hasOverlap: true,
    overlapStartDate: formatDateForAPI(overlapStart),
    overlapEndDate: formatDateForAPI(overlapEnd)
  }
}

/**
 * 날짜를 API 형식(YYYY-MM-DD)으로 포맷 (UTC+9 타임존 적용)
 * @param date 날짜 객체 또는 문자열
 * @returns YYYY-MM-DD 형식의 날짜 문자열
 */
export function formatDateForAPI(date: string | Date): string {
  // 문자열인 경우 UTC+9 타임존으로 해석
  if (typeof date === 'string') {
    // YYYY-MM-DD 형식인 경우 입력된 날짜를 그대로 반환 (UTC+9 기준으로 해석)
    if (/^\d{4}-\d{2}-\d{2}$/.test(date)) {
      return date // 입력된 날짜를 그대로 반환
    }
  }

  // Date 객체인 경우 UTC+9 타임존으로 해석하여 날짜 부분만 추출
  if (date instanceof Date) {
    // UTC+9 타임존으로 해석
    const utcPlus9 = new Date(date.getTime() + 9 * 60 * 60 * 1000)
    const year = utcPlus9.getUTCFullYear()
    const month = String(utcPlus9.getUTCMonth() + 1).padStart(2, '0')
    const day = String(utcPlus9.getUTCDate()).padStart(2, '0')
    return `${year}-${month}-${day}`
  }

  // 다른 형식의 문자열인 경우 기존 로직 사용
  const d = new Date(date)
  return d.toISOString().split('T')[0]
}

/**
 * 날짜를 한국어 형식으로 포맷 (UTC+9 타임존 적용)
 * @param date 날짜 객체 또는 문자열
 * @returns YYYY년 MM월 DD일 형식의 날짜 문자열
 */
export function formatDateForKorean(date: string | Date): string {
  // 문자열인 경우 UTC+9 타임존으로 해석
  if (typeof date === 'string') {
    // YYYY-MM-DD 형식인 경우 UTC+9 자정으로 해석
    if (/^\d{4}-\d{2}-\d{2}$/.test(date)) {
      const d = new Date(date + 'T00:00:00.000+09:00')
      const year = d.getUTCFullYear()
      const month = String(d.getUTCMonth() + 1).padStart(2, '0')
      const day = String(d.getUTCDate()).padStart(2, '0')
      return `${year}년 ${month}월 ${day}일`
    }
  }

  // Date 객체이거나 다른 형식의 문자열인 경우 기존 로직 사용
  const d = new Date(date)
  const year = d.getFullYear()
  const month = d.getMonth() + 1
  const day = d.getDate()
  return `${year}년 ${month}월 ${day}일`
}

/**
 * 두 날짜 사이의 일수 계산
 * @param startDate 시작일
 * @param endDate 종료일
 * @returns 일수 차이
 */
export function calculateDaysBetween(startDate: string | Date, endDate: string | Date): number {
  const start = new Date(startDate)
  const end = new Date(endDate)
  const diffTime = end.getTime() - start.getTime()
  return Math.ceil(diffTime / (1000 * 60 * 60 * 24))
}

/**
 * 현재 날짜를 API 형식으로 반환 (UTC+9 타임존 적용)
 * @returns 오늘 날짜 (YYYY-MM-DD)
 */
export function getCurrentDateForAPI(): string {
  // UTC+9 타임존의 현재 시간을 가져와서 API 형식으로 변환
  const currentUtc = getCurrentUTC()
  return formatDateForAPI(currentUtc)
}

/**
 * 날짜 유효성 검증
 * @param dateString 날짜 문자열
 * @returns 유효한 날짜인지 여부
 */
export function isValidDate(dateString: string): boolean {
  const date = new Date(dateString)
  return date instanceof Date && !isNaN(date.getTime())
}

/**
 * 날짜 범위 검증
 * @param startDate 시작일
 * @param endDate 종료일
 * @returns 시작일이 종료일보다 이른지 여부
 */
export function isValidDateRange(startDate: string | Date, endDate: string | Date): boolean {
  const start = new Date(startDate)
  const end = new Date(endDate)
  return start <= end
}

/**
 * 통일된 날짜 처리 유틸리티
 *
 * 이 모듈은 모든 날짜 처리를 표준화하여 UTC와 서울 시간을 일관되게 처리합니다.
 *
 * 표준:
 * - 데이터베이스 저장: UTC (ISO 8601)
 * - 사용자 표시: 서울 시간 (Asia/Seoul)
 * - 입력 처리: 다양한 형식 지원 → UTC로 변환
 * - API 응답: UTC → 서울 시간으로 변환
 */

/**
 * 표준화된 날짜 타입
 */
export type StandardDate = string & { readonly __brand: 'StandardDate' }

/**
 * 지원하는 날짜 입력 형식
 */
export type DateInputFormat =
  | string // ISO 8601, YYYY-MM-DD, YYYY.MM.DD 등
  | Date // JavaScript Date 객체
  | number // Unix timestamp, Excel 날짜 등

/**
 * 날짜 표시 형식
 */
export const DATE_FORMATS = {
  // 표시용 형식들
  DISPLAY: {
    FULL: 'YYYY. MM. DD.', // 2025. 01. 15.
    SHORT: 'MM/DD', // 01/15
    ISO: 'YYYY-MM-DD', // 2025-01-15
    KOREAN: 'YYYY년 MM월 DD일', // 2025년 01월 15일
    RELATIVE: 'relative' // 상대적 시간 (1일 전, 2시간 전 등)
  },

  // 입력용 형식들
  INPUT: {
    HTML_DATE: 'YYYY-MM-DD', // HTML date input
    DATETIME_LOCAL: 'YYYY-MM-DDTHH:mm', // HTML datetime-local input
    ISO: 'YYYY-MM-DDTHH:mm:ss.sssZ' // ISO 8601
  }
} as const

/**
 * 시간대 설정 import
 */
// timezone-config.js 파일이 삭제되어 import 제거됨

/**
 * 시간대 상수 (하위 호환성을 위해 유지)
 */
export const SEOUL_TIMEZONE = 'Asia/Seoul'
export const UTC_TIMEZONE = 'UTC'

/**
 * 현재 설정된 시간대를 가져옵니다.
 */
function getCurrentAppTimezone(): string {
  return SEOUL_TIMEZONE // 기본값으로 서울 시간대 사용
}

/**
 * 날짜를 UTC로 변환 (데이터베이스 저장용)
 * 모든 입력을 현재 설정된 시간대 기준으로 해석하여 UTC로 저장
 */
export function toUTC(date: DateInputFormat): StandardDate {
  if (!date) return '' as StandardDate

  try {
    let dateObj: Date
    const currentTimezone = getCurrentAppTimezone()
    const timezoneOffset = '+09:00' // 서울 시간대 오프셋

    if (date instanceof Date) {
      // Date 객체는 이미 올바른 시간대로 간주
      dateObj = date
    } else if (typeof date === 'number') {
      // Excel 날짜 또는 Unix timestamp 처리
      if (date > 25569) {
        // Excel 날짜 (1900-01-01 기준)
        const excelEpoch = new Date(1900, 0, 1)
        dateObj = new Date(excelEpoch.getTime() + (date - 2) * 24 * 60 * 60 * 1000)
      } else {
        // Unix timestamp
        dateObj = new Date(date * 1000)
      }
    } else {
      // 문자열 처리 - 모든 날짜를 현재 설정된 시간대 기준으로 해석
      const dateStr = String(date).trim()

      if (!dateStr) return '' as StandardDate

      // 다양한 형식 지원
      if (dateStr.includes('T')) {
        // ISO 8601 형식 - 이미 시간대 정보가 포함되어 있으면 그대로 사용
        if (dateStr.includes('+') || dateStr.includes('Z') || dateStr.includes('-', 10)) {
          dateObj = new Date(dateStr)
        } else {
          // 시간대 정보가 없으면 현재 설정된 시간대로 해석
          dateObj = new Date(`${dateStr}${timezoneOffset}`)
        }
      } else if (dateStr.includes('.')) {
        // YYYY.MM.DD 형식 - 현재 설정된 시간대 자정으로 해석
        const [year, month, day] = dateStr.split('.')
        dateObj = new Date(
          `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}T00:00:00${timezoneOffset}`
        )
      } else if (dateStr.includes('-')) {
        // YYYY-MM-DD 형식 - 현재 설정된 시간대 자정으로 해석
        const [year, month, day] = dateStr.split('-')
        dateObj = new Date(
          `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}T00:00:00${timezoneOffset}`
        )
      } else if (dateStr.includes('/')) {
        // MM/DD/YYYY 또는 DD/MM/YYYY 형식 - 현재 설정된 시간대 자정으로 해석
        const parts = dateStr.split('/')
        if (parts.length === 3) {
          // MM/DD/YYYY 가정
          const [month, day, year] = parts
          dateObj = new Date(
            `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}T00:00:00${timezoneOffset}`
          )
        }
      } else {
        // 기본 Date 생성자 사용 후 현재 설정된 시간대로 해석
        const tempDate = new Date(dateStr)
        if (!isNaN(tempDate.getTime())) {
          // 유효한 날짜면 현재 설정된 시간대 자정으로 재해석
          const year = tempDate.getFullYear()
          const month = String(tempDate.getMonth() + 1).padStart(2, '0')
          const day = String(tempDate.getDate()).padStart(2, '0')
          dateObj = new Date(`${year}-${month}-${day}T00:00:00${timezoneOffset}`)
        } else {
          dateObj = tempDate
        }
      }
    }

    if (isNaN(dateObj.getTime())) {
      throw new Error(`Invalid date: ${date}`)
    }

    // UTC로 변환하여 ISO 문자열 반환
    return dateObj.toISOString() as StandardDate
  } catch (error) {
    console.error('Date conversion error:', error, 'for input:', date)
    return '' as StandardDate
  }
}

/**
 * UTC 날짜를 서울 시간으로 변환하여 표시용 형식으로 포맷팅
 */
export function formatDateForDisplay(
  utcDate: StandardDate | string,
  format: keyof typeof DATE_FORMATS.DISPLAY = 'FULL'
): string {
  if (!utcDate) return ''

  try {
    const date = new Date(utcDate)

    if (isNaN(date.getTime())) {
      console.warn('Invalid UTC date for display:', utcDate)
      return ''
    }

    // 현재 설정된 시간대로 변환
    const currentTimezone = getCurrentAppTimezone()
    const localDate = new Date(date.toLocaleString('en-US', { timeZone: currentTimezone }))

    const year = localDate.getFullYear()
    const month = String(localDate.getMonth() + 1).padStart(2, '0')
    const day = String(localDate.getDate()).padStart(2, '0')
    const hours = String(localDate.getHours()).padStart(2, '0')
    const minutes = String(localDate.getMinutes()).padStart(2, '0')

    switch (format) {
      case 'FULL':
        return `${year}. ${month}. ${day}.`
      case 'SHORT':
        return `${month}/${day}`
      case 'ISO':
        return `${year}-${month}-${day}`
      case 'KOREAN':
        return `${year}년 ${month}월 ${day}일`
      case 'RELATIVE':
        return getRelativeTime(seoulDate)
      default:
        return `${year}. ${month}. ${day}.`
    }
  } catch (error) {
    console.error('Date display formatting error:', error, 'for date:', utcDate)
