/**
 * 팀 리더 여부 확인
 */
export function isTeamLeader(employee: Employee): boolean {
  const leaderPositions = [
    'CEO',
    'CFO',
    'CTO',
    '대표이사',
    '재무이사',
    '기술이사',
    '연구소장',
    '상무'
  ]
  return leaderPositions.includes(employee.position)
}

/**
 * 계약직 여부 확인
 */
export function isContractEmployee(employee: Employee): boolean {
  return employee.employmentType === 'contract'
}

/**
 * 퇴사 예정자 여부 확인
 */
export function isTerminationPending(employee: Employee): boolean {
  if (!employee.terminationDate || employee.status === 'terminated') {
    return false
  }

  const now = new Date()
  const terminationDate = new Date(employee.terminationDate)
  const oneMonthFromNow = new Date()
  oneMonthFromNow.setMonth(oneMonthFromNow.getMonth() + 1)

  return terminationDate >= now && terminationDate <= oneMonthFromNow
}

/**
 * 퇴사까지 남은 일수 계산
 */
export function getDaysUntilTermination(employee: Employee): number | null {
  if (!employee.terminationDate || employee.status === 'terminated') {
    return null
  }

  const now = new Date()
  const terminationDate = new Date(employee.terminationDate)
  const diffTime = terminationDate.getTime() - now.getTime()
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24))

  return diffDays
}

/**
 * 근속년수 계산
 */
export function getTenureYears(employee: Employee): number {
  const hireDate = new Date(employee.hireDate)
  const now = new Date()
  const diffTime = now.getTime() - hireDate.getTime()
  const diffYears = diffTime / (1000 * 60 * 60 * 24 * 365.25)

  return Math.floor(diffYears * 10) / 10 // 소수점 첫째 자리까지
}

/**
 * 근속년수 라벨 반환
 */
export function getTenureLabel(employee: Employee): string {
  const years = getTenureYears(employee)
  if (years < 1) {
    return '1년 미만'
  } else if (years < 2) {
    return '1년 이상 2년 미만'
  } else if (years < 5) {
    return '2년 이상 5년 미만'
  } else if (years < 10) {
    return '5년 이상 10년 미만'
  } else {
    return '10년 이상'
  }
}

// ===== 날짜 관련 유틸리티 =====

/**
 * 날짜를 한국식으로 포맷팅
 */
export function formatDate(dateString: string): string {
  if (!dateString) return ''
  const date = new Date(dateString)
  return date.toLocaleDateString('ko-KR', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  })
}

/**
 * 날짜를 간단한 형식으로 포맷팅 (YYYY-MM-DD)
 */
export function formatDateShort(dateString: string): string {
  if (!dateString) return ''
  const date = new Date(dateString)
  return date.toISOString().split('T')[0]
}

/**
 * 상대적 시간 표시 (예: "3일 전", "1주 후")
 */
export function getRelativeTime(dateString: string): string {
  if (!dateString) return ''
  const date = new Date(dateString)
  const now = new Date()
  const diffTime = date.getTime() - now.getTime()
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24))

  if (diffDays === 0) {
    return '오늘'
  } else if (diffDays === 1) {
    return '내일'
  } else if (diffDays === -1) {
    return '어제'
  } else if (diffDays > 0) {
    return `${diffDays}일 후`
  } else {
    return `${Math.abs(diffDays)}일 전`
  }
}

// ===== 검색 및 필터링 유틸리티 =====

/**
 * 직원 검색 (이름, 이메일, 사번)
 */
export function searchEmployees(employees: Employee[], query: string): Employee[] {
  if (!query.trim()) return employees

  const lowercaseQuery = query.toLowerCase()
  return employees.filter(
    emp =>
      emp.name.toLowerCase().includes(lowercaseQuery) ||
      emp.email.toLowerCase().includes(lowercaseQuery) ||
      emp.employeeId.toLowerCase().includes(lowercaseQuery)
  )
}

/**
 * 직원 필터링
 */
export function filterEmployees(
  employees: Employee[],
  filters: {
    department?: string
    position?: string
    status?: EmployeeStatus
    employmentType?: EmploymentType
    level?: EmployeeLevel
  }
): Employee[] {
  return employees.filter(emp => {
    if (filters.department && emp.department !== filters.department) return false
    if (filters.position && emp.position !== filters.position) return false
    if (filters.status && emp.status !== filters.status) return false
    if (filters.employmentType && emp.employmentType !== filters.employmentType) return false
    if (filters.level && emp.level !== filters.level) return false
    return true
  })
}

// ===== 정렬 유틸리티 =====

/**
 * 직원 정렬 옵션
 */
export type EmployeeSortOption =
  | 'name'
  | 'employeeId'
  | 'department'
  | 'position'
  | 'hireDate'
  | 'status'

/**
 * 직원 목록 정렬
 */
export function sortEmployees(
  employees: Employee[],
  sortBy: EmployeeSortOption,
  direction: 'asc' | 'desc' = 'asc'
): Employee[] {
  const sorted = [...employees].sort((a, b) => {
    let aValue: string | number
    let bValue: string | number

    switch (sortBy) {
      case 'name':
        aValue = a.name
        bValue = b.name
        break
      case 'employeeId':
        aValue = a.employeeId
        bValue = b.employeeId
        break
      case 'department':
        aValue = a.department
        bValue = b.department
        break
      case 'position':
        aValue = a.position
        bValue = b.position
        break
      case 'hireDate':
        aValue = new Date(a.hireDate).getTime()
        bValue = new Date(b.hireDate).getTime()
        break
      case 'status':
        aValue = a.status
        bValue = b.status
        break
      default:
        return 0
    }

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return direction === 'asc'
        ? aValue.localeCompare(bValue, 'ko')
        : bValue.localeCompare(aValue, 'ko')
    }

    if (aValue < bValue) return direction === 'asc' ? -1 : 1
    if (aValue > bValue) return direction === 'asc' ? 1 : -1
    return 0
  })

  return sorted
}

// ===== 통계 유틸리티 =====

/**
 * 부서별 직원 수 통계
 */
export function getDepartmentStats(employees: Employee[]): Record<string, number> {
  const stats: Record<string, number> = {}
  employees.forEach(emp => {
    stats[emp.department] = (stats[emp.department] || 0) + 1
  })
  return stats
}

/**
 * 직위별 직원 수 통계
 */
export function getPositionStats(employees: Employee[]): Record<string, number> {
  const stats: Record<string, number> = {}
  employees.forEach(emp => {
    stats[emp.position] = (stats[emp.position] || 0) + 1
  })
  return stats
}

/**
 * 고용 형태별 직원 수 통계
 */
export function getEmploymentTypeStats(employees: Employee[]): Record<EmploymentType, number> {
  const stats: Record<EmploymentType, number> = {
    'full-time': 0,
    'part-time': 0,
    contract: 0,
    intern: 0
  }
  employees.forEach(emp => {
    stats[emp.employmentType]++
  })
  return stats
}

// ===== 검증 유틸리티 =====

/**
 * 이메일 형식 검증
 */
export function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
  return emailRegex.test(email)
}

/**
 * 전화번호 형식 검증
 */
export function isValidPhone(phone: string): boolean {
  const phoneRegex = /^[0-9-+\s()]+$/
  return phoneRegex.test(phone) && phone.length >= 10
}

/**
 * 사번 형식 검증
 */
export function isValidEmployeeId(employeeId: string): boolean {
  return /^[A-Za-z0-9_-]+$/.test(employeeId) && employeeId.length >= 3
}

/**
 * 한국 이름 처리 유틸리티
 *
 * 이 모듈은 한국 이름의 다양한 형식을 표준화하고 처리하는 함수들을 제공합니다.
 *
 * 주요 기능:
 * - 한국 이름 감지 (isKoreanName)
 * - 이름 형식 변환 (processKoreanName, formatKoreanNameStandard)
 * - 성과 이름 분리 (splitKoreanName)
 * - 성+이름 결합 (formatKoreanName)
 *
 * 지원하는 입력 형식:
 * - "지은 차" → "차지은" (이름 성 → 성 이름)
 * - "차 지은" → "차지은" (성 이름 → 성 이름)
 * - "차지은" → "차지은" (이미 표준 형식)
 * - "John Doe" → "John Doe" (영문 이름은 그대로 유지)
 *
 * @example
 * ```typescript
 * import { formatKoreanNameStandard, splitKoreanName } from '$lib/utils/korean-name';
 *
 * // 이름 형식 변환
 * formatKoreanNameStandard("지은 차"); // "차지은"
 *
 * // 성과 이름 분리
 * splitKoreanName("차지은"); // { surname: "차", givenName: "지은" }
 * ```
 */

/**
 * 한국 이름인지 확인하는 함수
 * @param name 이름 문자열
 * @returns 한국 이름 여부
 */
export function isKoreanName(name: string): boolean {
  if (!name || typeof name !== 'string') return false

  // 한글 정규식 (가-힣)
  const koreanRegex = /^[가-힣\s]+$/
  return koreanRegex.test(name.trim())
}

/**
 * 한국 이름을 성+이름(띄어쓰기 없이) 형태로 변환
 * @param firstName 성
 * @param lastName 이름
 * @returns 가공된 한국 이름 또는 원본 이름
 */
export function formatKoreanName(firstName: string, lastName: string): string {
  if (!firstName || !lastName) return ''

  const fullName = `${firstName} ${lastName}`.trim()

  // 한국 이름인 경우에만 성+이름 형태로 변환
  if (isKoreanName(fullName)) {
    return `${firstName}${lastName}`
  }

  // 한국 이름이 아닌 경우 원본 형태 유지
  return fullName
}

/**
 * 전체 이름에서 한국 이름 부분만 가공
 * @param fullName 전체 이름 (예: "지은 차" 또는 "차지은")
 * @returns 가공된 이름 (예: "차지은")
 */
export function processKoreanName(fullName: string): string {
  if (!fullName || typeof fullName !== 'string') return ''

  const trimmed = fullName.trim()

  // 한국 이름인 경우
  if (isKoreanName(trimmed)) {
    // 공백으로 분리
    const parts = trimmed.split(/\s+/)
    if (parts.length === 2) {
      const [first, second] = parts

      // 일반적으로 성은 1글자, 이름은 2글자 이상
      if (first.length >= 2 && second.length === 1) {
        // "지은 차" -> "차지은" (이름 성 -> 성 이름)
        return `${second}${first}`
      } else if (first.length === 1 && second.length >= 2) {
        // "차 지은" -> "차지은" (이미 올바른 순서)
        return `${first}${second}`
      }

      // 기타 경우는 단순 결합
      return `${first}${second}`
    }
  }

  // 한국 이름이 아니거나 공백이 없는 경우 원본 반환
  return trimmed
}

/**
 * 한국 이름을 성과 이름으로 분리
 * @param fullName 전체 이름 (예: "차지은" 또는 "지은 차")
 * @returns { surname: string, givenName: string }
 */
export function splitKoreanName(fullName: string): { surname: string; givenName: string } {
  if (!fullName || typeof fullName !== 'string') return { surname: '', givenName: '' }

  const trimmed = fullName.trim()

  // 한국 이름인지 확인
  if (isKoreanName(trimmed)) {
    // 공백으로 분리된 경우 (예: "지은 차")
    const parts = trimmed.split(/\s+/)
    if (parts.length === 2) {
      const [first, second] = parts

      // 일반적으로 성은 1글자, 이름은 2글자 이상
      if (first.length >= 2 && second.length === 1) {
        // "지은 차" -> surname: "차", givenName: "지은"
        return { surname: second, givenName: first }
      } else if (first.length === 1 && second.length >= 2) {
        // "차 지은" -> surname: "차", givenName: "지은"
        return { surname: first, givenName: second }
      }
    }

    // 공백이 없는 경우 (예: "차지은")
    if (trimmed.length >= 2) {
      // 첫 글자를 성으로, 나머지를 이름으로 분리
      return {
        surname: trimmed.charAt(0),
        givenName: trimmed.slice(1)
      }
    }
  }

  // 영문 이름인 경우 공백으로 분리
  const parts = trimmed.split(' ')
  if (parts.length >= 2) {
    return {
      surname: parts[0],
      givenName: parts.slice(1).join(' ')
    }
  }

  // 분리할 수 없는 경우 전체를 이름으로
  return {
    surname: '',
    givenName: trimmed
  }
}

/**
 * 한국 이름을 표준 형식으로 포맷팅 (성+이름, 띄어쓰기 없음)
 * @param fullName 전체 이름 (다양한 형식 지원)
 * @returns 표준 형식의 한국 이름
 */
export function formatKoreanNameStandard(fullName: string): string {
  if (!fullName || typeof fullName !== 'string') return ''

  const trimmed = fullName.trim()

  // 이미 표준 형식인 경우 (띄어쓰기 없음)
  if (!trimmed.includes(' ')) {
    return trimmed
  }

  // 한국 이름인 경우 표준 형식으로 변환
  if (isKoreanName(trimmed)) {
    const { surname, givenName } = splitKoreanName(trimmed)
    return `${surname}${givenName}`
  }

  // 한국 이름이 아닌 경우 원본 반환
  return trimmed
}

/**
 * 이름 처리 검증 및 강제 유틸리티
 *
 * 이 모듈은 이름 처리가 표준 방식으로 이루어지도록 강제하는 도구들을 제공합니다.
 */

import { formatEmployeeName } from './format'
import { formatKoreanNameStandard } from './korean-name'

/**
 * 이름 표시를 위한 타입 가드
 * 모든 이름 표시는 이 타입을 사용해야 합니다.
 */
export type StandardizedName = string & { readonly __brand: 'StandardizedName' }

/**
 * 직원 객체에서 표준화된 이름을 생성
 */
export function createStandardEmployeeName(employee: {
  last_name?: string
  first_name?: string
}): StandardizedName {
  return formatEmployeeName(employee) as StandardizedName
}

/**
 * 전체 이름에서 표준화된 이름을 생성
 */
export function createStandardFullName(fullName: string): StandardizedName {
  return formatKoreanNameStandard(fullName) as StandardizedName
}

/**
 * 이름이 표준 형식인지 검증
 */
export function isValidStandardName(name: string): name is StandardizedName {
  // 한국 이름인 경우 (성)(이름) 형식이고 공백이 없어야 함
  const koreanRegex = /^[가-힣]+$/
  if (koreanRegex.test(name)) {
    return name.length >= 2 && !name.includes(' ')
  }

  // 영문 이름인 경우 그대로 허용
  return name.length > 0
}

/**
 * 이름 표시 강제 함수 (개발 모드에서만 동작)
 */
export function enforceStandardName(name: string, context: string = '이름'): StandardizedName {
  if (process.env.NODE_ENV === 'development') {
    if (!isValidStandardName(name)) {
      console.warn(`⚠️ [이름 표시 강제] ${context}에서 비표준 형식 발견: "${name}"`)
      console.warn('표준 형식으로 변환 중...')
    }
  }

  return formatKoreanNameStandard(name) as StandardizedName
}

/**
 * 런타임 검증을 위한 데코레이터 함수
 */
export function withNameValidation<T extends (...args: any[]) => any>(
  fn: T,
  context: string = '함수'
): T {
  return ((...args: any[]) => {
    const result = fn(...args)

    if (process.env.NODE_ENV === 'development') {
      // 결과가 문자열인 경우 이름 형식 검증
      if (typeof result === 'string' && result.length > 0) {
        // 한국 이름 패턴이 있는지 확인
        const koreanRegex = /[가-힣]/
        if (koreanRegex.test(result) && result.includes(' ')) {
          console.warn(`⚠️ [이름 검증] ${context}에서 비표준 형식 반환: "${result}"`)
          console.warn('표준 형식: (성)(이름) - 공백 없음')
        }
      }
    }

    return result
  }) as T
}

/**
 * 이름 처리 표준 가이드라인
 */
export const NAME_STANDARDS = {
  /**
   * 한국 이름 표준 형식: (성)(이름) - 공백 없음
   * 예: "차지은", "이지후", "김성호"
   */
  KOREAN_FORMAT: '(성)(이름) - 공백 없음',

