    // 팩터 업데이트
    const result = await query(
      `
			UPDATE global_factors 
			SET factor_value = $1, description = $2, updated_at = CURRENT_TIMESTAMP
			WHERE factor_name = $3
			RETURNING *
		`,
      [factorValue, description || null, factorName]
    )

    if (result.rows.length === 0) {
      return json(
        {
          success: false,
