// Project Management API - Projects
// 프로젝트 관리 시스템의 프로젝트 관련 API

import { query } from '$lib/database/connection'
import { transformArrayData, transformProjectData } from '$lib/utils/api-data-transformer'
import { json } from '@sveltejs/kit'
import type { RequestHandler } from './$types'

// 프로젝트 목록 조회
export const GET: RequestHandler = async ({ url }) => {
  try {
    const searchParams = url.searchParams
    const status = searchParams.get('status')
    const sponsorType = searchParams.get('sponsorType')
    const researchType = searchParams.get('researchType')
    const priority = searchParams.get('priority')
    const managerId = searchParams.get('managerId')
    const search = searchParams.get('search')
    const startDateFrom = searchParams.get('startDateFrom')
    const startDateTo = searchParams.get('startDateTo')

    let sqlQuery = `
			SELECT 
				p.*,
				e.first_name || ' ' || e.last_name as manager_name,
				COUNT(pm.id) as member_count,
				COALESCE(SUM(pm.participation_rate), 0) as total_participation_rate
			FROM projects p
			LEFT JOIN employees e ON p.manager_id = e.id
			LEFT JOIN project_members pm ON p.id = pm.project_id AND pm.status = 'active'
		`

    const conditions = []
    const params = []
    let paramIndex = 1

    if (status && status !== 'all') {
      conditions.push(`p.status = $${paramIndex++}`)
      params.push(status)
    }

    if (sponsorType && sponsorType !== 'all') {
      conditions.push(`p.sponsor_type = $${paramIndex++}`)
      params.push(sponsorType)
    }

    if (researchType && researchType !== 'all') {
      conditions.push(`p.research_type = $${paramIndex++}`)
      params.push(researchType)
    }

    if (priority && priority !== 'all') {
      conditions.push(`p.priority = $${paramIndex++}`)
      params.push(priority)
    }

    if (managerId) {
      conditions.push(`p.manager_id = $${paramIndex++}`)
      params.push(managerId)
    }

    if (search) {
      conditions.push(
        `(p.title ILIKE $${paramIndex} OR p.code ILIKE $${paramIndex} OR p.description ILIKE $${paramIndex})`
      )
      params.push(`%${search}%`)
      paramIndex++
    }

    if (startDateFrom) {
      conditions.push(`p.start_date >= $${paramIndex++}`)
      params.push(startDateFrom)
    }

    if (startDateTo) {
      conditions.push(`p.start_date <= $${paramIndex++}`)
      params.push(startDateTo)
    }

    if (conditions.length > 0) {
      sqlQuery += ' WHERE ' + conditions.join(' AND ')
    }

    sqlQuery += `
			GROUP BY p.id, e.first_name, e.last_name
			ORDER BY p.created_at DESC
		`

    const result = await query(sqlQuery, params)

    // 데이터 변환: snake_case를 camelCase로 변환
    const transformedData = transformArrayData(result.rows, transformProjectData)

    return json({
      success: true,
      data: transformedData,
      total: transformedData.length
    })
  } catch (error) {
    console.error('프로젝트 목록 조회 실패:', error)
    return json(
      {
        success: false,
        message: '프로젝트 목록을 불러오는데 실패했습니다.',
        error: error instanceof Error ? error.message : '알 수 없는 오류'
      },
      { status: 500 }
    )
  }
}

// 간소화된 프로젝트 생성
export const POST: RequestHandler = async ({ request }) => {
  try {
    const data = await request.json()
    const {
      code,
      title,
      description = '',
      status = 'planning' // 기본값을 '기획'으로 설정
    } = data

    // 필수 필드 검증
    if (!code || !title) {
      return json(
        {
          success: false,
          error: '프로젝트 코드와 제목은 필수입니다.'
        },
        { status: 400 }
      )
    }

    // 상태 값 검증 - 기획, 진행, 완료만 허용
    const validStatuses = ['planning', 'active', 'completed']
    if (!validStatuses.includes(status)) {
      return json(
        {
          success: false,
          error: '유효하지 않은 프로젝트 상태입니다. (기획, 진행, 완료 중 선택)'
        },
        { status: 400 }
      )
    }

    // 프로젝트 코드 중복 확인
    const existingProject = await query('SELECT id FROM projects WHERE code = $1', [code])

    if (existingProject.rows.length > 0) {
      return json(
        {
          success: false,
          error: '이미 존재하는 프로젝트 코드입니다.'
        },
        { status: 400 }
      )
    }

    // 간소화된 프로젝트 생성
    const result = await query(
      `
			INSERT INTO projects (
				code, title, description, status
			) VALUES ($1, $2, $3, $4)
			RETURNING *
		`,
      [code, title, description, status]
    )

    const project = result.rows[0]

    // 데이터 변환: snake_case를 camelCase로 변환
    const transformedProject = transformProjectData(project)

    return json({
      success: true,
      data: transformedProject,
      message: '프로젝트가 성공적으로 생성되었습니다.'
    })
  } catch (error) {
    console.error('프로젝트 생성 실패:', error)
    return json(
      {
        success: false,
        error: '프로젝트 생성에 실패했습니다.',
        details: error instanceof Error ? error.message : '알 수 없는 오류'
      },
      { status: 500 }
    )
  }
}

// Project Management API - Individual Project
// 개별 프로젝트 관련 API

import { query } from '$lib/database/connection'
import {
  transformArrayData,
  transformMilestoneData,
  transformProjectBudgetData,
  transformProjectData,
  transformProjectMemberData,
  transformRiskData
} from '$lib/utils/api-data-transformer'
import { json } from '@sveltejs/kit'
import type { RequestHandler } from './$types'

// 개별 프로젝트 조회
export const GET: RequestHandler = async ({ params }) => {
  try {
    const { id } = params

    // 프로젝트 기본 정보 조회
    const projectResult = await query(
      `
			SELECT 
				p.*,
				e.first_name || ' ' || e.last_name as manager_name,
				COUNT(pm.id) as member_count,
				COALESCE(SUM(pm.participation_rate), 0) as total_participation_rate
			FROM projects p
			LEFT JOIN employees e ON p.manager_id = e.id
			LEFT JOIN project_members pm ON p.id = pm.project_id AND pm.status = 'active'
			WHERE p.id = $1
			GROUP BY p.id, e.first_name, e.last_name
		`,
      [id]
    )

    if (projectResult.rows.length === 0) {
      return json(
        {
          success: false,
          message: '프로젝트를 찾을 수 없습니다.'
        },
        { status: 404 }
      )
    }

    const project = projectResult.rows[0]

    // 프로젝트 멤버 목록 조회
    const membersResult = await query(
      `
			SELECT 
				pm.*,
				e.first_name || ' ' || e.last_name as employee_name,
				e.department
			FROM project_members pm
			LEFT JOIN employees e ON pm.employee_id = e.id
			WHERE pm.project_id = $1
			ORDER BY pm.created_at DESC
		`,
      [id]
    )

    // 프로젝트 사업비 조회
    const budgetsResult = await query(
      `
			SELECT * FROM project_budgets
			WHERE project_id = $1
			ORDER BY period_number DESC
		`,
      [id]
    )

    // 프로젝트 마일스톤 조회
    const milestonesResult = await query(
      `
			SELECT * FROM project_milestones
			WHERE project_id = $1
			ORDER BY milestone_date ASC
		`,
      [id]
    )

    // 프로젝트 위험 요소 조회
    const risksResult = await query(
      `
			SELECT 
				pr.*,
				e.first_name || ' ' || e.last_name as owner_name
			FROM project_risks pr
			LEFT JOIN employees e ON pr.owner_id = e.id
			WHERE pr.project_id = $1
			ORDER BY pr.created_at DESC
		`,
      [id]
    )

    // 데이터 변환: snake_case를 camelCase로 변환
    const transformedProject = transformProjectData(project)
    const transformedMembers = transformArrayData(membersResult.rows, transformProjectMemberData)
    const transformedBudgets = transformArrayData(budgetsResult.rows, transformProjectBudgetData)
    const transformedMilestones = transformArrayData(milestonesResult.rows, transformMilestoneData)
    const transformedRisks = transformArrayData(risksResult.rows, transformRiskData)

    return json({
      success: true,
      data: {
        ...transformedProject,
        members: transformedMembers,
        budgets: transformedBudgets,
        milestones: transformedMilestones,
        risks: transformedRisks
      }
    })
  } catch (error) {
    console.error('프로젝트 조회 실패:', error)
    return json(
      {
        success: false,
        message: '프로젝트를 불러오는데 실패했습니다.',
        error: error instanceof Error ? error.message : '알 수 없는 오류'
      },
      { status: 500 }
    )
  }
}

// 프로젝트 수정
export const PUT: RequestHandler = async ({ params, request }) => {
  try {
    const { id } = params
    const data = await request.json()
    const {
      code,
      title,
      description,
      sponsor,
      sponsorName,
      sponsorType,
      startDate,
      endDate,
      managerId,
      budgetTotal,
      researchType,
      technologyArea,
      priority,
      status
    } = data

    // 프로젝트 존재 확인
    const existingProject = await query('SELECT id FROM projects WHERE id = $1', [id])

    if (existingProject.rows.length === 0) {
      return json(
        {
          success: false,
          message: '프로젝트를 찾을 수 없습니다.'
        },
        { status: 404 }
      )
    }

    // 프로젝트 코드 중복 확인 (자신 제외)
    if (code) {
      const duplicateCheck = await query('SELECT id FROM projects WHERE code = $1 AND id != $2', [
        code,
        id
      ])

      if (duplicateCheck.rows.length > 0) {
        return json(
          {
            success: false,
            message: '이미 존재하는 프로젝트 코드입니다.'
          },
          { status: 400 }
        )
      }
    }

    // 업데이트할 필드들 동적 생성
    const updateFields = []
    const updateValues = []
    let paramIndex = 1

    const fieldsToUpdate = {
      code,
      title,
      description,
      sponsor,
      sponsor_name: sponsorName,
      sponsor_type: sponsorType,
      start_date: startDate,
      end_date: endDate,
      manager_id: managerId,
      budget_total: budgetTotal,
      research_type: researchType,
      technology_area: technologyArea,
      priority,
      status
    }

    Object.entries(fieldsToUpdate).forEach(([key, value]) => {
      if (value !== undefined) {
        updateFields.push(`${key} = $${paramIndex++}`)
        updateValues.push(value)
      }
    })

    if (updateFields.length === 0) {
      return json(
        {
          success: false,
          message: '수정할 데이터가 없습니다.'
        },
        { status: 400 }
      )
    }

    updateFields.push(`updated_at = CURRENT_TIMESTAMP`)
    updateValues.push(id)

    const result = await query(
      `
			UPDATE projects 
			SET ${updateFields.join(', ')}
			WHERE id = $${paramIndex}
			RETURNING *
		`,
      updateValues
    )

    const updatedProject = result.rows[0]

    // 업데이트된 프로젝트 정보와 함께 반환
    const projectWithDetails = await query(
      `
			SELECT 
				p.*,
				e.first_name || ' ' || e.last_name as manager_name,
				COUNT(pm.id) as member_count,
				COALESCE(SUM(pm.participation_rate), 0) as total_participation_rate
			FROM projects p
			LEFT JOIN employees e ON p.manager_id = e.id
			LEFT JOIN project_members pm ON p.id = pm.project_id AND pm.status = 'active'
			WHERE p.id = $1
			GROUP BY p.id, e.first_name, e.last_name
		`,
      [id]
    )

    // 데이터 변환: snake_case를 camelCase로 변환
    const transformedProject = transformProjectData(projectWithDetails.rows[0])

    return json({
      success: true,
      data: transformedProject,
      message: '프로젝트가 성공적으로 수정되었습니다.'
    })
  } catch (error) {
    console.error('프로젝트 수정 실패:', error)
    return json(
      {
        success: false,
        message: '프로젝트 수정에 실패했습니다.',
        error: error instanceof Error ? error.message : '알 수 없는 오류'
      },
      { status: 500 }
    )
  }
}

// 프로젝트 삭제
export const DELETE: RequestHandler = async ({ params }) => {
  try {
    const { id } = params

    // 프로젝트 존재 확인
    const existingProject = await query('SELECT id, status FROM projects WHERE id = $1', [id])

    if (existingProject.rows.length === 0) {
      return json(
        {
          success: false,
          message: '프로젝트를 찾을 수 없습니다.'
        },
        { status: 404 }
      )
    }

    const project = existingProject.rows[0]

    // 프로젝트 삭제 가능 여부 확인 (모든 상태에서 삭제 가능)
    // 프로젝트 삭제 요청 처리

    // 트랜잭션으로 관련 데이터 모두 삭제
    await query('BEGIN')

    try {
      // 1. evidence_items 먼저 삭제 (project_budgets를 참조)
      await query(
        `
				DELETE FROM evidence_items 
				WHERE project_budget_id IN (
					SELECT id FROM project_budgets WHERE project_id = $1
				)
			`,
        [id]
      )

      // 2. project_budgets 삭제
      await query('DELETE FROM project_budgets WHERE project_id = $1', [id])

      // 3. 기타 관련 데이터 삭제
      await query('DELETE FROM participation_rate_history WHERE project_id = $1', [id])
      await query('DELETE FROM participation_rates WHERE project_id = $1', [id])
      await query('DELETE FROM project_members WHERE project_id = $1', [id])
      await query('DELETE FROM project_milestones WHERE project_id = $1', [id])
      await query('DELETE FROM project_risks WHERE project_id = $1', [id])

      // 4. rd_projects 테이블에서 삭제 (외래키 제약조건 해결)
      await query('DELETE FROM rd_projects WHERE project_id = $1', [id])

      // 5. 마지막으로 프로젝트 삭제
      await query('DELETE FROM projects WHERE id = $1', [id])

      await query('COMMIT')

      return json({
        success: true,
        message: '프로젝트가 성공적으로 삭제되었습니다.'
      })
    } catch (error) {
      await query('ROLLBACK')
      throw error
    }
  } catch (error) {
    console.error('프로젝트 삭제 실패:', error)
    return json(
      {
        success: false,
        message: '프로젝트 삭제에 실패했습니다.',
        error: error instanceof Error ? error.message : '알 수 없는 오류'
      },
      { status: 500 }
    )
  }
}

import { query } from '$lib/database/connection.js'
import type { AnnualBudget, AnnualBudgetFormData, BudgetSummary } from '$lib/types/project-budget'
import { formatDateForDisplay, toUTC } from '$lib/utils/date-handler.js'
import { json } from '@sveltejs/kit'
import type { RequestHandler } from './$types'

// GET: 연차별 예산 조회 (project_budgets 테이블 사용)
export const GET: RequestHandler = async ({ params }) => {
  try {
    const { projectId } = params

    // project_budgets 테이블에서 연차별 예산 조회 (연차별 예산용 칼럼 사용)
    const budgetResult = await query(
      `
			SELECT 
				id, project_id, period_number, start_date, end_date,
				government_funding_amount, company_cash_amount, company_in_kind_amount,
				created_at, updated_at
			FROM project_budgets 
			WHERE project_id = $1 
			ORDER BY period_number
			`,
      [projectId]
    )

    // 데이터 변환 및 처리 (연차별 예산용 칼럼 사용)
    const budgets: AnnualBudget[] = budgetResult.rows.map(row => {
      // 연차별 예산용 칼럼에서 직접 가져오기
      const governmentFunding = parseFloat(row.government_funding_amount) || 0
      const companyCash = parseFloat(row.company_cash_amount) || 0
      const companyInKind = parseFloat(row.company_in_kind_amount) || 0

      // 총 현금/현물 계산
      const totalCash = governmentFunding + companyCash // 정부지원금 + 기업부담금 현금
      const totalInKind = companyInKind // 기업부담금 현물
      const yearlyTotal = totalCash + totalInKind

      return {
        id: row.id,
        projectId: row.project_id,
        year: row.period_number, // period_number만 사용
        startDate: row.start_date ? formatDateForDisplay(row.start_date, 'ISO') : null,
        endDate: row.end_date ? formatDateForDisplay(row.end_date, 'ISO') : null,
        governmentFunding,
        governmentFundingCash: governmentFunding, // 연차별 예산에서는 정부지원금을 현금으로 간주
        governmentFundingInKind: 0, // 연차별 예산에서는 정부지원금을 현물로 구분하지 않음
        companyCash,
        companyInKind,
        totalCash,
        totalInKind,
        yearlyTotal,
        status: 'draft', // 기본값
        notes: '', // 기본값
        createdAt: row.created_at,
        updatedAt: row.updated_at
      }
    })

    // 예산 요약 계산
    const summary: BudgetSummary = {
      totalYears: budgets.length,
      totalGovernmentFunding: budgets.reduce((sum, b) => sum + b.governmentFunding, 0),
      totalCompanyCash: budgets.reduce((sum, b) => sum + b.companyCash, 0),
      totalCompanyInKind: budgets.reduce((sum, b) => sum + b.companyInKind, 0),
      totalCash: budgets.reduce((sum, b) => sum + b.totalCash, 0),
      totalInKind: budgets.reduce((sum, b) => sum + b.totalInKind, 0),
      totalBudget: budgets.reduce((sum, b) => sum + b.yearlyTotal, 0),
      governmentFundingRatio: 0,
      companyBurdenRatio: 0,
      cashRatio: 0,
      inKindRatio: 0
    }

    // 비율 계산
    if (summary.totalBudget > 0) {
      summary.governmentFundingRatio = (summary.totalGovernmentFunding / summary.totalBudget) * 100
      summary.companyBurdenRatio =
        ((summary.totalCompanyCash + summary.totalCompanyInKind) / summary.totalBudget) * 100
      summary.cashRatio = (summary.totalCash / summary.totalBudget) * 100
      summary.inKindRatio = (summary.totalInKind / summary.totalBudget) * 100
    }

    return json({
      success: true,
      data: {
        budgets,
        summary
      }
    })
  } catch (error) {
    console.error('연차별 예산 조회 실패:', error)
    return json(
      {
        success: false,
        error: '연차별 예산을 조회하는데 실패했습니다.'
      },
      { status: 500 }
    )
  }
}

// POST: 연차별 예산 생성 (project_budgets 테이블 사용)
export const POST: RequestHandler = async ({ params, request }) => {
  try {
    const { projectId } = params
    const { budgets }: { budgets: AnnualBudgetFormData[] } = await request.json()

    if (!budgets || budgets.length === 0) {
      return json(
        {
          success: false,
          error: '예산 데이터가 필요합니다.'
        },
        { status: 400 }
      )
    }

    // 기존 데이터 삭제
    await query('DELETE FROM project_budgets WHERE project_id = $1', [projectId])

    // 새 데이터 삽입 (연차별 예산용 칼럼 사용)
    for (const budget of budgets) {
      await query(
        `
				INSERT INTO project_budgets (
					project_id, period_number, start_date, end_date,
					government_funding_amount, company_cash_amount, company_in_kind_amount
				) VALUES ($1, $2, $3, $4, $5, $6, $7)
				`,
        [
          projectId,
          budget.year, // period_number
          budget.startDate ? toUTC(budget.startDate) : null,
          budget.endDate ? toUTC(budget.endDate) : null,
          budget.governmentFunding || 0,
          budget.companyCash || 0,
          budget.companyInKind || 0
        ]
      )
    }

    // 생성된 데이터 조회
    const result = await query(
      `
			SELECT 
				id, project_id, period_number, start_date, end_date,
				government_funding_amount, company_cash_amount, company_in_kind_amount,
				created_at, updated_at
			FROM project_budgets 
			WHERE project_id = $1 
			ORDER BY period_number
			`,
      [projectId]
    )

    const createdBudgets: AnnualBudget[] = result.rows.map(row => {
      // 연차별 예산용 칼럼에서 직접 가져오기
      const governmentFunding = parseFloat(row.government_funding_amount) || 0
      const companyCash = parseFloat(row.company_cash_amount) || 0
      const companyInKind = parseFloat(row.company_in_kind_amount) || 0

      // 총 현금/현물 계산
      const totalCash = governmentFunding + companyCash
      const totalInKind = companyInKind
      const yearlyTotal = totalCash + totalInKind

      return {
        id: row.id,
        projectId: row.project_id,
        year: row.period_number,
        startDate: row.start_date ? formatDateForDisplay(row.start_date, 'ISO') : null,
        endDate: row.end_date ? formatDateForDisplay(row.end_date, 'ISO') : null,
        governmentFunding,
        governmentFundingCash: governmentFunding,
        governmentFundingInKind: 0,
        companyCash,
        companyInKind,
        totalCash,
        totalInKind,
        yearlyTotal,
        status: 'draft',
        notes: '',
        createdAt: row.created_at,
        updatedAt: row.updated_at
      }
    })

    return json({
      success: true,
      data: {
        budgets: createdBudgets
      },
      message: '연차별 예산이 성공적으로 생성되었습니다.'
    })
  } catch (error) {
    console.error('연차별 예산 생성 실패:', error)
    return json(
      {
        success: false,
        error: '연차별 예산 생성에 실패했습니다.'
      },
      { status: 500 }
    )
  }
}

// PUT: 연차별 예산 수정 (project_budgets 테이블 사용)
export const PUT: RequestHandler = async ({ params, request }) => {
  try {
    const { projectId } = params
    const { year, budgetData }: { year: number; budgetData: AnnualBudgetFormData } =
      await request.json()

    // 입력 검증
