async function checkTypeScriptErrors() {
  logSection('TypeScript ì˜¤ë¥˜ ì²´í¬')

  try {
    logInfo('Svelte íƒ€ì… ì²´í¬ ì‹¤í–‰ ì¤‘...')
    const result = execSync('npm run check', {
      encoding: 'utf8',
      stdio: 'pipe'
    })

