async function generateErrorReport(tsResult, linterResult, buildResult) {
  logSection('ì˜¤ë¥˜ ë¦¬í¬íŠ¸')

  const totalErrors =
    tsResult.errors.length + linterResult.errors.length + buildResult.errors.length

  if (totalErrors === 0) {
