  }

  if (buildResult.errors.length > 0) {
    logError(`  - ë¹Œë“œ ì˜¤ë¥˜: ${buildResult.errors.length}ê°œ`)
  }

  logInfo('\nğŸ’¡ ê¶Œì¥ì‚¬í•­:')
