  }

  generateReport() {
    logSection('í’ˆì§ˆ ê²Œì´íŠ¸ ë¦¬í¬íŠ¸')

    const allPassed = Object.values(this.results).every(result =>
      typeof result === 'boolean' ? result : result.passed
