    // ê²°ê³¼ ì¶œë ¥
    logInfo('TypeScript: ' + (this.results.typescript.passed ? 'âœ… í†µê³¼' : 'âŒ ì‹¤íŒ¨'))
    logInfo(`  - ì˜¤ë¥˜: ${this.results.typescript.errors}ê°œ`)
