import { config } from 'dotenv'
import type { PoolClient, QueryResult } from 'pg'
import { Pool } from 'pg'
import { logger } from '$lib/utils/logger'

// Load environment variables
config()

// Database connection pool
let pool: Pool | null = null

// Database configuration - AWS only
const getDbConfig = () => {
  return {
    host: process.env.AWS_DB_HOST || 'db-viahub.cdgqkcss8mpj.ap-northeast-2.rds.amazonaws.com',
    port: parseInt(process.env.AWS_DB_PORT || '5432'),
    database: process.env.AWS_DB_NAME || 'postgres',
    user: process.env.AWS_DB_USER || 'postgres',
    password: process.env.AWS_DB_PASSWORD || 'viahubdev',
    ssl: {
      rejectUnauthorized: false
    }
  }
}

const dbConfig = {
  ...getDbConfig(),
  max: 20, // Maximum number of clients in the pool
  idleTimeoutMillis: 30000, // Close idle clients after 30 seconds
  connectionTimeoutMillis: 10000, // Return an error after 10 seconds if connection could not be established
  acquireTimeoutMillis: 10000 // Return an error after 10 seconds if client could not be acquired from pool
}

// Initialize database connection pool
export function initializeDatabase(): Pool {
  if (!pool) {
    pool = new Pool(dbConfig)

    // Handle pool errors
    pool.on('error', err => {
      logger.error('Unexpected error on idle client', err)
      process.exit(-1)
    })

    // Database connection pool initialized
  }

  return pool
}

// Get database connection
export async function getConnection(): Promise<PoolClient> {
  if (!pool) {
    try {
      initializeDatabase()
    } catch (error) {
      logger.error('Failed to initialize database connection:', error)
      throw error
    }
  }

  return await pool!.connect()
}

// Execute a query with parameters
export async function query<T extends Record<string, unknown> = any>(
  text: string,
  params?: unknown[]
): Promise<QueryResult<T>> {
  const client = await getConnection()
  try {
    const result = await client.query<T>(text, params)
    return result
  } finally {
    client.release()
  }
}

// Execute a transaction
export async function transaction<T>(callback: (client: PoolClient) => Promise<T>): Promise<T> {
  const client = await getConnection()
  try {
    await client.query('BEGIN')
    const result = await callback(client)
    await client.query('COMMIT')
    return result
  } catch (error) {
    await client.query('ROLLBACK')
    throw error
  } finally {
    client.release()
  }
}

// Close database connection pool
export async function closeDatabase(): Promise<void> {
  if (pool) {
    await pool.end()
    pool = null
    // Database connection pool closed
  }
}

// Database health check
export async function healthCheck(): Promise<boolean> {
  try {
    // Starting database health check
    const result = await query('SELECT 1 as health')
    const isHealthy = result.rows[0]?.health === 1

    return isHealthy
  } catch (error) {
    logger.error('Database health check failed:', error)
    logger.error('Error details:', {
      message: error instanceof Error ? error.message : 'Unknown error',
      code: (error as any)?.code,
      detail: (error as any)?.detail
    })
    return false
  }
}

