// ëª©í‘œ ë§ˆê°ì¼ ì•Œë¦¼ ì²´í¬
export function checkGoalDeadlines(budgetGoals: unknown[]) {
  const now = new Date()
  const oneWeekFromNow = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000)

  budgetGoals.forEach(goal => {
    const deadline = new Date(goal.deadline)
    const progress = (goal.currentAmount / goal.targetAmount) * 100

    if (deadline <= oneWeekFromNow && deadline > now && progress < 100) {
      const daysLeft = Math.ceil((deadline.getTime() - now.getTime()) / (24 * 60 * 60 * 1000))

      addNotification({
        type: 'warning',
        title: 'ëª©í‘œ ë§ˆê°ì¼ ì„ë°•',
        message: `${goal.name} ëª©í‘œ ë§ˆê°ê¹Œì§€ ${daysLeft}ì¼ ë‚¨ì•˜ìŠµë‹ˆë‹¤. (ì§„í–‰ë¥ : ${progress.toFixed(1)}%)`,
        actionUrl: '/finance',
        actionText: 'ëª©í‘œ í™•ì¸'
      })
    }
  })
}

