// ê°ì‚¬ ë¡œê·¸ í•¨ìˆ˜
export function logAudit(
  action: string,
  entity: string,
  entityId: string,
  oldData: any,
  newData: any
): void {
  const auditLog: AuditLog = {
    id: crypto.randomUUID(),
    actorId: 'current-user', // ì‹¤ì œë¡œëŠ” í˜„ì¬ ì‚¬ìš©ì ID
    action,
    entity,
    entityId,
    diff: { old: oldData, new: newData },
    at: new Date().toISOString()
  }

  auditLogs.update(list => [...list, auditLog])
}

