// ê°œì¸ë³„ ì´ ì°¸ì—¬ìœ¨ ê³„ì‚° (íŠ¹ì • ì›”)
export function getPersonTotalParticipationRate(personId: string, month: string): number {
  const participations = getPersonMonthlyParticipation(personId, month)
