// ê°œì¸ í”„ë¡œí•„ ë¶„ì„
function analyzePersonProfile(personId: string): any {
  // ì‹¤ì œ êµ¬í˜„ì—ì„œëŠ” ê°œì¸ ë°ì´í„°ë¥¼ ë¶„ì„
  return {
    skills: ['JavaScript', 'React', 'Node.js', 'MongoDB'],
