  }
}

// ëŒ€ì²´ì í›„ë³´ ìŠ¤ì½”ì–´ë§
function scoreReplacementCandidates(
  projectRequirements: any,
  departingPersonProfile: any,
  urgency: string
): Array<{
  personId: string
  score: number
  reason: string
  availability: number
}> {
  // ì‹¤ì œ êµ¬í˜„ì—ì„œëŠ” ëª¨ë“  ì§ì›ì„ ëŒ€ìƒìœ¼ë¡œ ìŠ¤ì½”ì–´ë§
  const candidates = [
    {
      personId: 'person-1',
      name: 'ê¹€ëŒ€ì²´',
      skills: ['JavaScript', 'React', 'Node.js', 'PostgreSQL'],
      experienceLevel: 'senior',
      department: 'ê°œë°œíŒ€',
      currentParticipation: 60,
      salary: 5500000,
      performance: 'excellent',
      availability: 90
    },
    {
      personId: 'person-2',
      name: 'ì´ëŒ€ì²´',
      skills: ['JavaScript', 'Vue.js', 'Python', 'MySQL'],
      experienceLevel: 'mid',
      department: 'ê°œë°œíŒ€',
      currentParticipation: 40,
      salary: 4500000,
      performance: 'good',
      availability: 85
    },
    {
      personId: 'person-3',
      name: 'ë°•ëŒ€ì²´',
      skills: ['TypeScript', 'React', 'Express', 'MongoDB'],
      experienceLevel: 'senior',
      department: 'ê°œë°œíŒ€',
      currentParticipation: 70,
      salary: 5800000,
      performance: 'excellent',
      availability: 75
    }
  ]

  return candidates
    .map(candidate => {
      const score = calculateCandidateScore(
        candidate,
        projectRequirements,
        departingPersonProfile,
        urgency
      )

      return {
        personId: candidate.personId,
        score: score.total,
        reason: score.reason,
        availability: candidate.availability
      }
    })
    .sort((a, b) => b.score - a.score)
}

// í›„ë³´ì ì ìˆ˜ ê³„ì‚°
function calculateCandidateScore(
  candidate: any,
  projectRequirements: any,
  departingPersonProfile: any,
  urgency: string
): { total: number; reason: string } {
  let totalScore = 0
  const reasons: string[] = []

  // 1. ê¸°ìˆ  ìŠ¤í‚¬ ë§¤ì¹­ (40%)
  const skillScore = calculateSkillScore(candidate.skills, projectRequirements.requiredSkills)
  totalScore += skillScore * 0.4
  reasons.push(`ê¸°ìˆ  ìŠ¤í‚¬: ${skillScore}ì `)

  // 2. ê²½í—˜ ìˆ˜ì¤€ ë§¤ì¹­ (20%)
  const experienceScore = calculateExperienceScore(
    candidate.experienceLevel,
    projectRequirements.experienceLevel
  )
  totalScore += experienceScore * 0.2
  reasons.push(`ê²½í—˜ ìˆ˜ì¤€: ${experienceScore}ì `)

  // 3. ê°€ìš©ì„± (20%)
  const availabilityScore = calculateAvailabilityScore(
    candidate.availability,
    projectRequirements.participationRate
  )
  totalScore += availabilityScore * 0.2
  reasons.push(`ê°€ìš©ì„±: ${availabilityScore}ì `)

  // 4. ì„±ê³¼ ì´ë ¥ (10%)
  const performanceScore = calculatePerformanceScore(candidate.performance)
  totalScore += performanceScore * 0.1
  reasons.push(`ì„±ê³¼: ${performanceScore}ì `)

  // 5. ê¸‰ì—¬ ì ì •ì„± (10%)
  const salaryScore = calculateSalaryScore(candidate.salary, departingPersonProfile.salary)
  totalScore += salaryScore * 0.1
  reasons.push(`ê¸‰ì—¬ ì ì •ì„±: ${salaryScore}ì `)

  // ê¸´ê¸‰ë„ ë³´ì •
  if (urgency === 'critical') {
    totalScore *= 1.2
  } else if (urgency === 'high') {
    totalScore *= 1.1
  }

  return {
    total: Math.round(totalScore),
    reason: reasons.join(', ')
  }
}

// ê¸°ìˆ  ìŠ¤í‚¬ ì ìˆ˜ ê³„ì‚°
function calculateSkillScore(candidateSkills: string[], requiredSkills: string[]): number {
  const matchedSkills = candidateSkills.filter(skill => requiredSkills.includes(skill))
  return (matchedSkills.length / requiredSkills.length) * 100
}

// ê²½í—˜ ìˆ˜ì¤€ ì ìˆ˜ ê³„ì‚°
function calculateExperienceScore(candidateLevel: string, requiredLevel: string): number {
  const levelScores = {
    intern: 20,
    junior: 40,
    mid: 60,
    senior: 80,
    lead: 90,
    manager: 100
  }

  const candidateScore = levelScores[candidateLevel as keyof typeof levelScores] || 0
  const requiredScore = levelScores[requiredLevel as keyof typeof levelScores] || 0

  if (candidateScore >= requiredScore) {
    return 100
  } else {
    return (candidateScore / requiredScore) * 100
  }
}

// ê°€ìš©ì„± ì ìˆ˜ ê³„ì‚°
function calculateAvailabilityScore(
  candidateAvailability: number,
  requiredParticipation: number
): number {
  if (candidateAvailability >= requiredParticipation) {
    return 100
  } else {
    return (candidateAvailability / requiredParticipation) * 100
  }
}

// ì„±ê³¼ ì ìˆ˜ ê³„ì‚°
function calculatePerformanceScore(performance: string): number {
  const performanceScores = {
    excellent: 100,
    good: 80,
    average: 60,
    below_average: 40,
    poor: 20
  }

  return performanceScores[performance as keyof typeof performanceScores] || 0
}

// ê¸‰ì—¬ ì ì •ì„± ì ìˆ˜ ê³„ì‚°
function calculateSalaryScore(candidateSalary: number, departingPersonSalary: number): number {
  const ratio = candidateSalary / departingPersonSalary

  if (ratio <= 0.8) {
    return 100 // 20% ì´ìƒ ì €ë ´
  } else if (ratio <= 1.0) {
    return 90 // ë™ì¼í•˜ê±°ë‚˜ ì €ë ´
  } else if (ratio <= 1.2) {
    return 70 // 20% ì´ë‚´ ë¹„ìŒˆ
  } else {
    return 50 // 20% ì´ìƒ ë¹„ìŒˆ
  }
}

// ëŒ€ì²´ ì¶”ì²œ ìŠ¹ì¸
export function approveReplacementRecommendation(
  recommendationId: string,
  approvedPersonId: string,
  approverId: string,
  comment?: string
): void {
  replacementRecommendations.update(list => {
    const index = list.findIndex(r => r.id === recommendationId)
    if (index === -1) return list

    const recommendation = list[index]
    const updatedRecommendation = {
      ...recommendation,
      status: 'approved' as const,
      approvedBy: approverId,
      approvedAt: new Date().toISOString()
    }

    const newList = [...list]
    newList[index] = updatedRecommendation

    logAudit(
      'approve',
      'replacement_recommendation',
      recommendationId,
      {
        approvedPersonId,
        approverId,
        comment
      },
      updatedRecommendation
    )

    // ìŠ¹ì¸ëœ ëŒ€ì²´ì ë°°ì • ì²˜ë¦¬
    processReplacementAssignment(
      recommendation.projectId,
      recommendation.departingPersonId,
      approvedPersonId,
      recommendation.createdAt
    )

    return newList
  })
}

// ëŒ€ì²´ì ë°°ì • ì²˜ë¦¬
function processReplacementAssignment(
  projectId: string,
  departingPersonId: string,
  replacementPersonId: string,
  effectiveDate: string
): void {
  // 1. ê¸°ì¡´ ì°¸ì—¬ ë°°ì • ì¢…ë£Œ
  // 2. ìƒˆë¡œìš´ ì°¸ì—¬ ë°°ì • ìƒì„±
  // 3. ê¸‰ì—¬ ë³€ë™ ì²˜ë¦¬
  // 4. ì•Œë¦¼ ë°œì†¡

  logAudit(
    'process_replacement',
    'participation_assignment',
    projectId,
    {
      departingPersonId,
      replacementPersonId,
      effectiveDate
    },
    {}
  )
}

// ëŒ€ì²´ ì¶”ì²œ ê±°ë¶€
export function rejectReplacementRecommendation(
  recommendationId: string,
  rejectorId: string,
  reason: string
): void {
  replacementRecommendations.update(list => {
    const index = list.findIndex(r => r.id === recommendationId)
    if (index === -1) return list

    const recommendation = list[index]
    const updatedRecommendation = {
