// ë¯¸ì œì¶œ ì—°êµ¬ë…¸íŠ¸ ì²´í¬
export function getMissingResearchNotes(
  projectId: string,
  startDate: string,
  endDate: string
): {
  missingWeeks: string[]
  missingAuthors: string[]
} {
  const start = new Date(startDate)
  const end = new Date(endDate)
  const missingWeeks: string[] = []
  const missingAuthors: string[] = []

  // ì£¼ì°¨ë³„ë¡œ ì²´í¬
  const currentDate = new Date(start)
  while (currentDate <= end) {
    const weekOf = getWeekOfYear(currentDate)
    const weekNotes = getResearchNotesByWeek(projectId, weekOf)

    if (weekNotes.length === 0) {
      missingWeeks.push(weekOf)
    }

    currentDate.setDate(currentDate.getDate() + 7)
  }

  // ì‘ì„±ìë³„ ì²´í¬ (ì‹¤ì œ êµ¬í˜„ì—ì„œëŠ” í”„ë¡œì íŠ¸ ì°¸ì—¬ì ëª©ë¡ì„ ê°€ì ¸ì™€ì•¼ í•¨)
  // ì—¬ê¸°ì„œëŠ” ê°„ë‹¨íˆ ì²˜ë¦¬

  return { missingWeeks, missingAuthors }
}

// ì£¼ì°¨ ê³„ì‚° (ISO 8601 ê¸°ì¤€)
function getWeekOfYear(date: Date): string {
  const year = date.getFullYear()
  const firstDayOfYear = new Date(year, 0, 1)
  const pastDaysOfYear = (date.getTime() - firstDayOfYear.getTime()) / 86400000
  const weekNumber = Math.ceil((pastDaysOfYear + firstDayOfYear.getDay() + 1) / 7)
  return `${year}-W${weekNumber.toString().padStart(2, '0')}`
}

// ì—°êµ¬ë…¸íŠ¸ ì œì¶œ í˜„í™©
export function getResearchNoteSubmissionStatus(
  projectId: string,
  month: string
): {
  totalWeeks: number
  submittedWeeks: number
  submissionRate: number
  missingWeeks: string[]
} {
  const startDate = new Date(month + '-01')
  const endDate = new Date(startDate.getFullYear(), startDate.getMonth() + 1, 0)

  const { missingWeeks } = getMissingResearchNotes(
    projectId,
    startDate.toISOString(),
    endDate.toISOString()
  )

  // í•´ë‹¹ ì›”ì˜ ì´ ì£¼ì°¨ ìˆ˜ ê³„ì‚°
  const totalWeeks = Math.ceil(
    (endDate.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24 * 7)
  )
  const submittedWeeks = totalWeeks - missingWeeks.length
  const submissionRate = totalWeeks > 0 ? (submittedWeeks / totalWeeks) * 100 : 0

  return {
    totalWeeks,
    submittedWeeks,
    submissionRate,
    missingWeeks
  }
}

