async function generateBundleContent(
  bundleId: string,
  projectId: string,
  period: string
): Promise<void> {
  try {
    // 1. í”„ë¡œì íŠ¸ ì •ë³´ ìˆ˜ì§‘
    const projectInfo = await collectProjectInfo(projectId)

    // 2. ë¬¸ì„œ ìˆ˜ì§‘
