/**
 * í•œêµ­ ì´ë¦„ì„ í‘œì¤€ í˜•ì‹ìœ¼ë¡œ í¬ë§·íŒ… (ì„±+ì´ë¦„, ë„ì–´ì“°ê¸° ì—†ìŒ)
 */
function formatKoreanNameStandard(fullName: string): string {
  if (!fullName || typeof fullName !== 'string') return ''

  const trimmed = fullName.trim()

  // ì´ë¯¸ í‘œì¤€ í˜•ì‹ì¸ ê²½ìš° (ë„ì–´ì“°ê¸° ì—†ìŒ)
  if (!trimmed.includes(' ')) {
    return trimmed
  }

  // í•œêµ­ ì´ë¦„ì¸ì§€ í™•ì¸ (í•œê¸€ ì •ê·œì‹)
  const koreanRegex = /^[ê°€-í£\s]+$/
  if (koreanRegex.test(trimmed)) {
    // ê³µë°±ìœ¼ë¡œ ë¶„ë¦¬
    const parts = trimmed.split(/\s+/)
    if (parts.length === 2) {
      const [first, second] = parts

      // ì¼ë°˜ì ìœ¼ë¡œ ì„±ì€ 1ê¸€ì, ì´ë¦„ì€ 2ê¸€ì ì´ìƒ
      if (first.length >= 2 && second.length === 1) {
        // "ì§€ì€ ì°¨" -> "ì°¨ì§€ì€" (ì´ë¦„ ì„± -> ì„± ì´ë¦„)
        return formatEmployeeName({ last_name: second, first_name: first })
      } else if (first.length === 1 && second.length >= 2) {
