  }

  /**
   * Git í›… ì„¤ì •
   */
  private static setupGitHooks(): void {
    logger.log('ğŸ”— [Git í›…] ì„¤ì • ì‹œì‘')

    // pre-commit í›… ì„¤ì •
    const preCommitHook = `#!/bin/sh
# ìë™ ê²€ì¦ ì‹¤í–‰
echo "ğŸ” [Git í›…] ì»¤ë°‹ ì „ ê²€ì¦ ì‹œì‘"
node -e "
const { AutoValidationHooks } = require('./src/lib/utils/auto-validation-hooks.ts');
