 */
export const SEOUL_TIMEZONE = 'Asia/Seoul'
export const UTC_TIMEZONE = 'UTC'

/**
 * í˜„ì¬ ì„¤ì •ëœ ì‹œê°„ëŒ€ë¥¼ ê°€ì ¸ì˜µë‹ˆë‹¤.
 */
function getCurrentAppTimezone(): string {
  return SEOUL_TIMEZONE // ê¸°ë³¸ê°’ìœ¼ë¡œ ì„œìš¸ ì‹œê°„ëŒ€ ì‚¬ìš©
