  return formatKoreanNameStandard(name) as StandardizedName
}

/**
 * ëŸ°íƒ€ì„ ê²€ì¦ì„ ìœ„í•œ ë°ì½”ë ˆì´í„° í•¨ìˆ˜
 */
export function withNameValidation<T extends (...args: unknown[]) => any>(
  fn: T,
  context: string = 'í•¨ìˆ˜'
): T {
  return ((...args: unknown[]) => {
    const result = fn(...args)

    if (process.env.NODE_ENV === 'development') {
      // ê²°ê³¼ê°€ ë¬¸ìì—´ì¸ ê²½ìš° ì´ë¦„ í˜•ì‹ ê²€ì¦
      if (typeof result === 'string' && result.length > 0) {
        // í•œêµ­ ì´ë¦„ íŒ¨í„´ì´ ìˆëŠ”ì§€ í™•ì¸
        const koreanRegex = /[ê°€-í£]/
        if (koreanRegex.test(result) && result.includes(' ')) {
          logger.warn(`âš ï¸ [ì´ë¦„ ê²€ì¦] ${context}ì—ì„œ ë¹„í‘œì¤€ í˜•ì‹ ë°˜í™˜: "${result}"`)
          logger.warn('í‘œì¤€ í˜•ì‹: (ì„±)(ì´ë¦„) - ê³µë°± ì—†ìŒ')
        }
      }
    }

    return result
  }) as T
}

