// ì¡°ì§ë„ ë°ì´í„° ìƒì„± (ë™ì )
export const GET: RequestHandler = async () => {
  try {
    // ëª¨ë“  ì§ì› ë°ì´í„° ì¡°íšŒ (ì§ì±… ì •ë³´ í¬í•¨)
    const employeesResult = await query(`
			SELECT 
				e.id,
				e.first_name,
				e.last_name,
				e.email,
				e.department,
				e.position,
				e.salary,
				e.status,
				e.job_title_id,
				jt.name as job_title_name,
				jt.level as job_title_level,
				jt.category as job_title_category
			FROM employees e
			LEFT JOIN job_titles jt ON e.job_title_id = jt.id
			WHERE e.status = 'active'
			ORDER BY e.department, e.position
		`)

    const employees = Array.isArray(employeesResult) ? employeesResult : employeesResult.rows || []

    // ë¶€ì„œë³„ë¡œ ì§ì› ê·¸ë£¹í™”
    const departmentGroups: { [key: string]: unknown[] } = {}
    employees.forEach((emp: any) => {
      const dept = emp.department || 'ê¸°íƒ€'
      if (!departmentGroups[dept]) {
        departmentGroups[dept] = []
      }
      departmentGroups[dept].push({
        name: `${emp.last_name}${emp.first_name}`,
        position: emp.position,
        email: emp.email,
        salary: emp.salary,
        job_title: emp.job_title_name,
        isTeamLead: emp.job_title_name === 'Team Lead'
      })
    })

    // ë¶€ì„œë¥¼ ì„ì›ë³„ë¡œ ê·¸ë£¹í™”í•˜ëŠ” ë§¤í•‘
    const executiveDepartmentMapping: { [key: string]: string[] } = {
      ëŒ€í‘œì´ì‚¬: ['ê²½ì˜ê¸°íšíŒ€'],
      ì¬ë¬´ì´ì‚¬: ['ê²½ì˜ì§€ì›íŒ€'],
