    // ì „ì²´ ê²€ì¦ ê²°ê³¼ ìƒì„±
    const overallValidation = ValidationUtils.createOverallValidation(validationResults)

    logger.log(`âœ… [ì˜ˆì‚° ì¼ê´€ì„± ê²€ì¦] ì™„ë£Œ - ${validation.isValid ? 'âœ… í†µê³¼' : 'âŒ ì‹¤íŒ¨'}`)

