export const POST: RequestHandler = async ({ request }) => {
  try {
    const { assigneeId, dueDate, projectBudgetId } = await request.json()

    if (!assigneeId || !dueDate || !projectBudgetId) {
      return json({ error: 'ë‹´ë‹¹ì ID, ê¸°í•œ, í”„ë¡œì íŠ¸ ì˜ˆì‚° IDê°€ í•„ìš”í•©ë‹ˆë‹¤.' }, { status: 400 })
    }

    // ë‹´ë‹¹ìì˜ ì¬ì§ ì •ë³´ ì¡°íšŒ
    const employeeResult = await pool.query(
      `
			SELECT 
				id,
				first_name,
				last_name,
				hire_date,
				termination_date,
				status
			FROM employees 
			WHERE id = $1
		`,
      [assigneeId]
    )

    if (employeeResult.rows.length === 0) {
      return json({
        success: false,
        error: 'EMPLOYEE_NOT_FOUND',
        message: 'ë‹´ë‹¹ ì§ì›ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
      })
    }

    const employee = employeeResult.rows[0]
    const dueDateObj = new Date(dueDate)
    const hireDate = employee.hire_date ? new Date(employee.hire_date) : null
    const terminationDate = employee.termination_date ? new Date(employee.termination_date) : null

    // í”„ë¡œì íŠ¸ ì˜ˆì‚° ê¸°ê°„ ì¡°íšŒ
    const budgetResult = await pool.query(
      `
			SELECT 
				start_date,
				end_date,
				period_number,
				fiscal_year
			FROM project_budgets 
			WHERE id = $1
		`,
      [projectBudgetId]
    )

    if (budgetResult.rows.length === 0) {
      return json({
        success: false,
        error: 'BUDGET_NOT_FOUND',
        message: 'í”„ë¡œì íŠ¸ ì˜ˆì‚° ì •ë³´ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
      })
    }

    const budget = budgetResult.rows[0]
    const periodStartDate = new Date(budget.start_date)
    const periodEndDate = new Date(budget.end_date)

    // ì¬ì§ ê¸°ê°„ ê²€ì¦
    let isValid = true
    let reason = 'VALID'
    let message = 'ì¬ì§ ê¸°ê°„ì´ ìœ íš¨í•©ë‹ˆë‹¤.'
    const warnings = []

    // 1. í‡´ì‚¬í•œ ì§ì›ì¸ì§€ í™•ì¸
    if (employee.status === 'terminated' || terminationDate) {
      if (terminationDate && dueDateObj > terminationDate) {
        isValid = false
        reason = 'TERMINATED_BEFORE_DUE_DATE'
        message = `í‡´ì‚¬ì¼(${formatDateForDisplay(terminationDate.toISOString(), 'KOREAN')}) ì´í›„ì— ì¸ê±´ë¹„ë¥¼ ì§‘í–‰í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.`
      }
    }

    // 2. ì…ì‚¬ ì „ì— ì¸ê±´ë¹„ê°€ ì§‘í–‰ë˜ì—ˆëŠ”ì§€ í™•ì¸
    if (hireDate && dueDateObj < hireDate) {
      isValid = false
      reason = 'HIRED_AFTER_DUE_DATE'
      message = `ì…ì‚¬ì¼(${formatDateForDisplay(hireDate.toISOString(), 'KOREAN')}) ì´ì „ì— ì¸ê±´ë¹„ë¥¼ ì§‘í–‰í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.`
    }

    // 3. í˜„ì¬ ë¹„í™œì„± ìƒíƒœì¸ ì§ì›ì¸ì§€ í™•ì¸
    if (employee.status === 'inactive') {
      isValid = false
      reason = 'INACTIVE_EMPLOYEE'
      message = 'ë¹„í™œì„± ìƒíƒœì¸ ì§ì›ì—ê²Œ ì¸ê±´ë¹„ë¥¼ ì§‘í–‰í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
    }

    // 4. í”„ë¡œì íŠ¸ ê¸°ê°„ê³¼ ì¬ì§ ê¸°ê°„ì´ ê²¹ì¹˜ëŠ”ì§€ í™•ì¸
    if (hireDate && periodEndDate < hireDate) {
      isValid = false
      reason = 'HIRED_AFTER_PROJECT_PERIOD'
      message = `í”„ë¡œì íŠ¸ ê¸°ê°„(${formatDateForDisplay(periodStartDate.toISOString(), 'KOREAN')} ~ ${formatDateForDisplay(periodEndDate.toISOString(), 'KOREAN')}) ì´í›„ì— ì…ì‚¬í•œ ì§ì›ì…ë‹ˆë‹¤.`
    }

    if (terminationDate && periodStartDate > terminationDate) {
      isValid = false
      reason = 'TERMINATED_BEFORE_PROJECT_PERIOD'
      message = `í”„ë¡œì íŠ¸ ê¸°ê°„(${formatDateForDisplay(periodStartDate.toISOString(), 'KOREAN')} ~ ${formatDateForDisplay(periodEndDate.toISOString(), 'KOREAN')}) ì´ì „ì— í‡´ì‚¬í•œ ì§ì›ì…ë‹ˆë‹¤.`
    }

    // 5. ê²½ê³ ì‚¬í•­ ì²´í¬ (ìœ íš¨í•˜ì§€ë§Œ ì£¼ì˜ê°€ í•„ìš”í•œ ê²½ìš°)
    if (isValid) {
      // í‡´ì‚¬ ì˜ˆì •ì¸ ì§ì›
      if (terminationDate && dueDateObj > terminationDate) {
        warnings.push(
          `í‡´ì‚¬ ì˜ˆì •ì¼(${formatDateForDisplay(terminationDate.toISOString(), 'KOREAN')}) ì´í›„ì˜ ì¸ê±´ë¹„ì…ë‹ˆë‹¤.`
        )
      }

      // ì…ì‚¬í•œ ì§€ ì–¼ë§ˆ ì•ˆ ëœ ì§ì›
      if (hireDate) {
        const daysSinceHire = Math.floor(
          (dueDateObj.getTime() - hireDate.getTime()) / (1000 * 60 * 60 * 24)
        )
        if (daysSinceHire < 30) {
          warnings.push(`ì…ì‚¬í•œ ì§€ ${daysSinceHire}ì¼ë°–ì— ì•ˆ ëœ ì§ì›ì…ë‹ˆë‹¤.`)
        }
      }
    }

    return json({
      success: true,
      validation: {
        isValid,
        reason,
        message,
        warnings
      },
      employee: {
        id: employee.id,
        name: `${employee.last_name}${employee.first_name}`,
        hireDate: employee.hire_date,
        terminationDate: employee.termination_date,
        status: employee.status
      },
      projectPeriod: {
        startDate: budget.start_date,
        endDate: budget.end_date,
        periodNumber: budget.period_number,
        fiscalYear: budget.fiscal_year
      },
      dueDate: dueDate
    })
  } catch (error) {
    logger.error('Employment validation error:', error)
    return json(
      {
        success: false,
        error: 'VALIDATION_ERROR',
        message: 'ì¬ì§ ê¸°ê°„ ê²€ì¦ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.',
        details: error instanceof Error ? error.message : 'Unknown error'
      },
      { status: 500 }
    )
  }
}

