			FROM project_members pm
			JOIN employees e ON pm.employee_id = e.id
			JOIN projects p ON pm.project_id = p.id
			WHERE 1=1
		`

    const params: unknown[] = []
    let paramIndex = 1

    if (projectId) {
      sqlQuery += ` AND pm.project_id = $${paramIndex}`
