// POST /api/project-management/project-members - í”„ë¡œì íŠ¸ ë©¤ë²„ ì¶”ê°€
export const POST: RequestHandler = async ({ request }) => {
  try {
    const data = await request.json()
    const {
      projectId,
      employeeId,
      role = 'researcher',
      startDate,
      endDate,
      participationRate = 100,
      contributionType = 'cash',
      contractAmount = 0,
      status = 'active'
    } = data

    // í•„ìˆ˜ í•„ë“œ ê²€ì¦
    if (!projectId || !employeeId) {
      return json(
        {
          success: false,
          message: 'í”„ë¡œì íŠ¸ IDì™€ ì§ì› IDëŠ” í•„ìˆ˜ì…ë‹ˆë‹¤.'
        },
        { status: 400 }
      )
    }

    // ì°¸ì—¬ìœ¨ ê²€ì¦ (0-100 ì‚¬ì´)
    if (participationRate < 0 || participationRate > 100) {
      return json(
        {
          success: false,
          message: 'ì°¸ì—¬ìœ¨ì€ 0-100 ì‚¬ì´ì˜ ê°’ì´ì–´ì•¼ í•©ë‹ˆë‹¤.'
        },
        { status: 400 }
      )
    }

    // ì¤‘ë³µ ê²€ì‚¬
    const existingMember = await query(
      'SELECT id FROM project_members WHERE project_id = $1 AND employee_id = $2',
      [projectId, employeeId]
    )

    if (existingMember.rows.length > 0) {
      return json(
        {
          success: false,
          message: 'í•´ë‹¹ ì§ì›ì€ ì´ë¯¸ ì´ í”„ë¡œì íŠ¸ì˜ ë©¤ë²„ì…ë‹ˆë‹¤.'
        },
        { status: 400 }
      )
    }

    // í•´ë‹¹ ì§ì›ì˜ í”„ë¡œì íŠ¸ ì°¸ì—¬ ê¸°ê°„ì— ìœ íš¨í•œ ê¸‰ì—¬ ê³„ì•½ì„œ ì¡°íšŒ
    // í”„ë¡œì íŠ¸ ì°¸ì—¬ ê¸°ê°„ê³¼ ê³„ì•½ì„œ ê¸°ê°„ì´ ê²¹ì¹˜ëŠ” ê²½ìš°ë¥¼ ì°¾ìŒ
    const contractResult = await query(
      `
			SELECT sc.annual_salary, sc.monthly_salary
			FROM salary_contracts sc
			WHERE sc.employee_id = $1
				AND sc.status = 'active'
				AND (
					-- ê³„ì•½ì„œ ì‹œì‘ì¼ì´ í”„ë¡œì íŠ¸ ì°¸ì—¬ ê¸°ê°„ ë‚´ì— ìˆê±°ë‚˜
					(sc.start_date <= COALESCE($3, CURRENT_DATE) AND (sc.end_date IS NULL OR sc.end_date >= COALESCE($2, CURRENT_DATE)))
					OR
					-- í”„ë¡œì íŠ¸ ì°¸ì—¬ ê¸°ê°„ì´ ê³„ì•½ì„œ ê¸°ê°„ ë‚´ì— ìˆê±°ë‚˜
					(COALESCE($2, CURRENT_DATE) <= sc.start_date AND COALESCE($3, CURRENT_DATE) >= sc.start_date)
				)
			ORDER BY sc.start_date DESC
			LIMIT 1
		`,
      [employeeId, startDate, endDate]
    )

    // ê³„ì•½ì„œì—ì„œ ì—°ë´‰ì„ ê°€ì ¸ì˜¤ê±°ë‚˜, ì œê³µëœ ê³„ì•½ê¸ˆì•¡ ì‚¬ìš©
    let finalContractAmount = contractAmount
    if (contractResult.rows.length > 0) {
      // ì—°ë´‰ì„ ì›”ê¸‰ìœ¼ë¡œ ë³€í™˜ (ì—°ë´‰ / 12)
      finalContractAmount = contractResult.rows[0].annual_salary / 12
    } else {
      // ê³„ì•½ì„œê°€ ì—†ëŠ” ê²½ìš°, í•´ë‹¹ ì§ì›ì˜ ëª¨ë“  ê³„ì•½ì„œ ì •ë³´ë¥¼ ì¡°íšŒí•˜ì—¬ ì•ˆë‚´ ë©”ì‹œì§€ ìƒì„±
      const allContractsResult = await query(
        `
				SELECT sc.start_date, sc.end_date, sc.annual_salary, sc.status
				FROM salary_contracts sc
				WHERE sc.employee_id = $1
				ORDER BY sc.start_date DESC
			`,
        [employeeId]
      )

      if (allContractsResult.rows.length === 0) {
        return json(
          {
            success: false,
            message:
              'í•´ë‹¹ ì§ì›ì˜ ê¸‰ì—¬ ê³„ì•½ì„œê°€ ë“±ë¡ë˜ì§€ ì•Šì•˜ìŠµë‹ˆë‹¤. ê¸‰ì—¬ ê³„ì•½ì„œë¥¼ ë¨¼ì € ë“±ë¡í•´ì£¼ì„¸ìš”.',
            errorCode: 'NO_CONTRACT'
          },
          { status: 400 }
        )
      } else {
        // ê³„ì•½ì„œëŠ” ìˆì§€ë§Œ ê¸°ê°„ì´ ë§ì§€ ì•ŠëŠ” ê²½ìš°
        const contracts = allContractsResult.rows
        const projectStartDate = startDate ? new Date(startDate) : new Date()
        const projectEndDate = endDate ? new Date(endDate) : new Date()

        // ê°€ì¥ ê°€ê¹Œìš´ ê³„ì•½ì„œ ì°¾ê¸°
        const futureContracts = contracts.filter(c => new Date(c.start_date) > projectStartDate)
        const pastContracts = contracts.filter(c => new Date(c.start_date) <= projectStartDate)

        let message = `í”„ë¡œì íŠ¸ ì°¸ì—¬ ê¸°ê°„(${startDate || 'ì‹œì‘ì¼ ë¯¸ì •'} ~ ${endDate || 'ì¢…ë£Œì¼ ë¯¸ì •'})ì— í•´ë‹¹ ì§ì›ì´ ì¬ì§ ì¤‘ì´ ì•„ë‹™ë‹ˆë‹¤.\n\n`

        if (futureContracts.length > 0) {
          const nextContract = futureContracts[futureContracts.length - 1] // ê°€ì¥ ê°€ê¹Œìš´ ë¯¸ë˜ ê³„ì•½
          const contractStartDate = formatDateForKorean(nextContract.start_date)
          message += `ë‹¤ìŒ ê³„ì•½ ì‹œì‘ì¼: ${contractStartDate}\n`
          message += `í•´ë‹¹ ë‚ ì§œë¶€í„° í”„ë¡œì íŠ¸ ì°¸ì—¬ê°€ ê°€ëŠ¥í•©ë‹ˆë‹¤.`
        } else if (pastContracts.length > 0) {
          const lastContract = pastContracts[0]
          if (lastContract.end_date) {
            const contractEndDate = formatDateForKorean(lastContract.end_date)
            message += `ë§ˆì§€ë§‰ ê³„ì•½ ì¢…ë£Œì¼: ${contractEndDate}\n`
            message += `í•´ë‹¹ ì§ì›ì€ ì´ë¯¸ í‡´ì‚¬í•œ ìƒíƒœì…ë‹ˆë‹¤.`
          } else {
            message += `ê³„ì•½ì„œ ìƒíƒœë¥¼ í™•ì¸í•´ì£¼ì„¸ìš”.`
          }
        }

        return json(
          {
            success: false,
            message: message,
            errorCode: 'CONTRACT_PERIOD_MISMATCH',
            contracts: contracts.map(c => ({
              startDate: c.start_date,
              endDate: c.end_date,
              status: c.status
            }))
          },
          { status: 400 }
        )
      }
    }

    // ì‹¤ì œ ê·¼ë¡œê³„ì•½ì„œì—ì„œ ì›”ê¸‰ ê°€ì ¸ì˜¤ê¸°
    let contractMonthlySalary = 0
    if (contractResult.rows.length > 0) {
      const contract = contractResult.rows[0]
      contractMonthlySalary = contract.monthly_salary || contract.annual_salary / 12
    }

    // ì›”ê°„ ê¸ˆì•¡ ê³„ì‚°: ì¤‘ì•™í™”ëœ ê¸‰ì—¬ ê³„ì‚° í•¨ìˆ˜ ì‚¬ìš©
    const monthlyAmount = calculateMonthlySalary(
      contractMonthlySalary * 12, // ì—°ë´‰ìœ¼ë¡œ ë³€í™˜
      participationRate
    )

    // í”„ë¡œì íŠ¸ ë©¤ë²„ ì¶”ê°€ (contract_amount ì œê±°)
    const result = await query(
      `
			INSERT INTO project_members (project_id, employee_id, role, start_date, end_date, participation_rate, contribution_type, monthly_amount, status)
			VALUES ($1, $2, $3, $4, $5, $6, $7, $8, $9)
			RETURNING *
		`,
      [
        projectId,
        employeeId,
        role,
        startDate,
        endDate,
        participationRate,
        contributionType,
        monthlyAmount,
        status
      ]
    )

    // ì¶”ê°€ëœ ë©¤ë²„ ì •ë³´ì™€ ê´€ë ¨ ì •ë³´ ì¡°íšŒ
    const memberWithDetails = await query(
      `
			SELECT 
				pm.*,
				CASE 
					WHEN e.first_name ~ '^[ê°€-í£]+$' AND e.last_name ~ '^[ê°€-í£]+$' 
					THEN CONCAT(e.last_name, e.first_name)
					ELSE CONCAT(e.first_name, ' ', e.last_name)
				END as employee_name,
				e.first_name,
				e.last_name,
				e.email as employee_email,
				e.department as employee_department,
				e.position as employee_position,
				p.title as project_title,
				p.code as project_code
			FROM project_members pm
			JOIN employees e ON pm.employee_id = e.id
			JOIN projects p ON pm.project_id = p.id
			WHERE pm.id = $1
		`,
      [result.rows[0].id]
    )

    // TIMESTAMP ë°ì´í„°ë¥¼ YYYY-MM-DD í˜•ì‹ìœ¼ë¡œ ë³€í™˜ (ì¤‘ì•™í™”ëœ í•¨ìˆ˜ ì‚¬ìš©)
    const memberData = memberWithDetails.rows[0]
    const formattedMemberData = {
      ...memberData,
      start_date: formatDateForAPI(memberData.start_date),
      end_date: formatDateForAPI(memberData.end_date)
    }

    return json({
      success: true,
      data: formattedMemberData,
      message: 'í”„ë¡œì íŠ¸ ë©¤ë²„ê°€ ì„±ê³µì ìœ¼ë¡œ ì¶”ê°€ë˜ì—ˆìŠµë‹ˆë‹¤.'
    })
  } catch (error) {
    logger.error('í”„ë¡œì íŠ¸ ë©¤ë²„ ì¶”ê°€ ì‹¤íŒ¨:', error)
    return json(
      {
        success: false,
        message: 'í”„ë¡œì íŠ¸ ë©¤ë²„ ì¶”ê°€ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.',
        error: (error as Error).message
      },
      { status: 500 }
    )
  }
}

