// PUT /api/project-management/project-members/[id] - í”„ë¡œì íŠ¸ ë©¤ë²„ ìˆ˜ì •
export const PUT: RequestHandler = async ({ params, request }) => {
  try {
    const data = await request.json()
    const {
      role,
      startDate,
      endDate,
      participationRate,
      contributionType,
      contractAmount,
      status
    } = data

    // í•„ìˆ˜ í•„ë“œ ê²€ì¦
    if (
      role === undefined &&
      startDate === undefined &&
      endDate === undefined &&
      participationRate === undefined &&
      contributionType === undefined &&
      contractAmount === undefined &&
      status === undefined
    ) {
      return json(
        {
          success: false,
          message: 'ìˆ˜ì •í•  í•„ë“œê°€ ì—†ìŠµë‹ˆë‹¤.'
        },
        { status: 400 }
      )
    }

    // ì°¸ì—¬ìœ¨ ê²€ì¦ (0-100 ì‚¬ì´)
    if (participationRate !== undefined && (participationRate < 0 || participationRate > 100)) {
      return json(
        {
          success: false,
          message: 'ì°¸ì—¬ìœ¨ì€ 0-100 ì‚¬ì´ì˜ ê°’ì´ì–´ì•¼ í•©ë‹ˆë‹¤.'
        },
        { status: 400 }
      )
    }

    // ë©¤ë²„ ì¡´ì¬ í™•ì¸
    const existingMember = await query('SELECT * FROM project_members WHERE id = $1', [params.id])

    if (existingMember.rows.length === 0) {
      return json(
        {
          success: false,
          message: 'í”„ë¡œì íŠ¸ ë©¤ë²„ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
        },
        { status: 404 }
      )
    }

    // ë©¤ë²„ ìˆ˜ì •
    const updateFields = []
    const updateValues = []
    let paramIndex = 1

    if (role !== undefined) {
      updateFields.push(`role = $${paramIndex}`)
      updateValues.push(role)
      paramIndex++
    }

    // ì°¸ì—¬ê¸°ê°„ ìˆ˜ì • ì‹œ UTC+9 íƒ€ì„ì¡´ ì ìš© ë° ìœ íš¨ì„± ê²€ì¦
    if (startDate !== undefined) {
      // ë‚ ì§œ ìœ íš¨ì„± ê²€ì¦
      if (!isValidDate(startDate)) {
        return json(
          {
            success: false,
            message: 'ìœ íš¨í•˜ì§€ ì•Šì€ ì‹œì‘ì¼ í˜•ì‹ì…ë‹ˆë‹¤.'
          },
          { status: 400 }
        )
      }

      // UTC+9 íƒ€ì„ì¡´ ì ìš© (TIMESTAMP íƒ€ì…ìœ¼ë¡œ ì €ì¥)
      const formattedStartDate = new Date(startDate + 'T00:00:00.000+09:00')
      updateFields.push(`start_date = $${paramIndex}`)
      updateValues.push(formattedStartDate)
      paramIndex++
    }

    if (endDate !== undefined) {
      // ë‚ ì§œ ìœ íš¨ì„± ê²€ì¦
      if (!isValidDate(endDate)) {
        return json(
          {
            success: false,
            message: 'ìœ íš¨í•˜ì§€ ì•Šì€ ì¢…ë£Œì¼ í˜•ì‹ì…ë‹ˆë‹¤.'
          },
          { status: 400 }
        )
      }

      // UTC+9 íƒ€ì„ì¡´ ì ìš© (TIMESTAMP íƒ€ì…ìœ¼ë¡œ ì €ì¥)
      const formattedEndDate = new Date(endDate + 'T23:59:59.999+09:00')
      updateFields.push(`end_date = $${paramIndex}`)
      updateValues.push(formattedEndDate)
      paramIndex++
    }

    // ì‹œì‘ì¼ê³¼ ì¢…ë£Œì¼ì´ ëª¨ë‘ ë³€ê²½ë˜ëŠ” ê²½ìš° ë‚ ì§œ ë²”ìœ„ ê²€ì¦
    if (startDate !== undefined && endDate !== undefined) {
      if (!isValidDateRange(startDate, endDate)) {
        return json(
          {
            success: false,
            message: 'ì‹œì‘ì¼ì´ ì¢…ë£Œì¼ë³´ë‹¤ ëŠ¦ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
          },
          { status: 400 }
        )
      }

      // í”„ë¡œì íŠ¸ ê¸°ê°„ê³¼ì˜ ê²¹ì¹¨ ê²€ì¦
      const currentMember = existingMember.rows[0]
      const projectResult = await query('SELECT start_date, end_date FROM projects WHERE id = $1', [
        currentMember.project_id
      ])

      if (projectResult.rows.length > 0) {
        const project = projectResult.rows[0]
        const participationValidation = calculateParticipationPeriod(
          startDate,
          endDate,
          project.start_date,
          project.end_date
        )

        if (!participationValidation.isValid) {
          return json(
            {
              success: false,
              message:
                participationValidation.errorMessage || 'ì°¸ì—¬ê¸°ê°„ì´ í”„ë¡œì íŠ¸ ê¸°ê°„ê³¼ ë§ì§€ ì•ŠìŠµë‹ˆë‹¤.'
            },
            { status: 400 }
          )
        }
      }
    }

    if (participationRate !== undefined) {
      updateFields.push(`participation_rate = $${paramIndex}`)
      updateValues.push(participationRate)
      paramIndex++
    }

    if (contributionType !== undefined) {
      updateFields.push(`contribution_type = $${paramIndex}`)
      updateValues.push(contributionType)
      paramIndex++
    }

    // contract_amount í•„ë“œ ì œê±° - ì‹¤ì œ ê·¼ë¡œê³„ì•½ì„œì—ì„œ ì¡°íšŒ

    if (status !== undefined) {
      updateFields.push(`status = $${paramIndex}`)
