  }
}

// ê¸‰ì—¬ ê³„ì•½ ê´€ë¦¬ API ì—”ë“œí¬ì¸íŠ¸

import { query } from '$lib/database/connection.js'
import type {
  CreateSalaryContractRequest,
  PaginatedResponse,
  SalaryContract
} from '$lib/types/salary-contracts'
import { formatDateForDisplay, toUTC } from '$lib/utils/date-handler.js'
import { json } from '@sveltejs/kit'
import type { RequestHandler } from './$types'

// GET: ê¸‰ì—¬ ê³„ì•½ ëª©ë¡ ì¡°íšŒ
export const GET: RequestHandler = async ({ url }) => {
  try {
    // ì¿¼ë¦¬ íŒŒë¼ë¯¸í„° íŒŒì‹±
    const page = parseInt(url.searchParams.get('page') || '1')
    const limit = parseInt(url.searchParams.get('limit') || '50')
    const employeeId = url.searchParams.get('employeeId') || ''
    const department = url.searchParams.get('department') || ''
    const position = url.searchParams.get('position') || ''
    const contractType = url.searchParams.get('contractType') || ''
    const status = url.searchParams.get('status') || ''
    const startDateFrom = url.searchParams.get('startDateFrom') || ''
    const startDateTo = url.searchParams.get('startDateTo') || ''
    const search = url.searchParams.get('search') || ''

    // WHERE ì¡°ê±´ êµ¬ì„±
    const conditions: string[] = []
    const params: unknown[] = []
    let paramIndex = 1

    if (employeeId) {
      conditions.push(`sc.employee_id = $${paramIndex}`)
      params.push(employeeId)
      paramIndex++
    }

    if (department) {
      conditions.push(`e.department = $${paramIndex}`)
      params.push(department)
      paramIndex++
    }

    if (position) {
      conditions.push(`e.position = $${paramIndex}`)
      params.push(position)
      paramIndex++
    }

    if (contractType) {
      conditions.push(`sc.contract_type = $${paramIndex}`)
      params.push(contractType)
      paramIndex++
    }

    if (status) {
      conditions.push(`sc.status = $${paramIndex}`)
      params.push(status)
      paramIndex++
    }

    if (startDateFrom) {
      conditions.push(`sc.start_date >= $${paramIndex}`)
      params.push(startDateFrom)
      paramIndex++
    }

    if (startDateTo) {
      conditions.push(`sc.start_date <= $${paramIndex}`)
      params.push(startDateTo)
      paramIndex++
    }

    if (search) {
      conditions.push(`(
				CONCAT(e.last_name, e.first_name) ILIKE $${paramIndex} OR 
				e.employee_id ILIKE $${paramIndex} OR 
				e.department ILIKE $${paramIndex} OR
				e.position ILIKE $${paramIndex}
			)`)
      params.push(`%${search}%`)
      paramIndex++
    }

    const whereClause = conditions.length > 0 ? `WHERE ${conditions.join(' AND ')}` : ''

    // ì „ì²´ ê°œìˆ˜ ì¡°íšŒ
    const countResult = await query(
      `
			SELECT COUNT(*) as total
			FROM salary_contracts sc
			JOIN employees e ON sc.employee_id = e.id
			${whereClause}
		`,
      params
    )

    const total = parseInt(countResult.rows[0]?.total || '0')
    const totalPages = Math.ceil(total / limit)
    const offset = (page - 1) * limit

    // ê¸‰ì—¬ ê³„ì•½ ëª©ë¡ ì¡°íšŒ (ìµœì í™”ëœ ì¿¼ë¦¬)
    const result = await query(
      `
			SELECT 
				sc.id,
				sc.employee_id,
				sc.start_date,
				sc.end_date,
				sc.annual_salary,
				sc.monthly_salary,
				sc.contract_type,
				sc.status,
				sc.notes,
				sc.created_at,
				sc.updated_at,
				sc.created_by,
				e.last_name || e.first_name as employee_name,
				e.employee_id as employee_id_number,
				e.department,
				e.position
			FROM salary_contracts sc
			INNER JOIN employees e ON sc.employee_id = e.id
			${whereClause}
			ORDER BY sc.start_date DESC, sc.created_at DESC
			LIMIT $${paramIndex} OFFSET $${paramIndex + 1}
		`,
      [...params, limit, offset]
    )

    const contracts: SalaryContract[] = result.rows.map(row => {
      return {
        id: row.id,
        employeeId: row.employee_id,
        startDate: row.start_date ? formatDateForDisplay(row.start_date, 'ISO') : '',
        endDate: row.end_date ? formatDateForDisplay(row.end_date, 'ISO') : '',
        annualSalary: parseFloat(row.annual_salary),
        monthlySalary: parseFloat(row.monthly_salary),
        contractType: row.contract_type,
        status: row.status,
        notes: row.notes,
        createdAt: row.created_at,
        updatedAt: row.updated_at,
        createdBy: row.created_by,
        employeeName: row.employee_name,
        employeeIdNumber: row.employee_id_number,
        department: row.department,
        position: row.position,
        contractEndDisplay: row.contract_end_display,
        statusDisplay: row.status_display
      }
    })

    const response: PaginatedResponse<SalaryContract> = {
      data: contracts,
      total,
      page,
      limit,
      totalPages
    }

    return json({
      success: true,
      data: response
    })
  } catch (error) {
    return json(
      {
        success: false,
        error: 'ê¸‰ì—¬ ê³„ì•½ ëª©ë¡ì„ ê°€ì ¸ì˜¤ëŠ”ë° ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.'
      },
      { status: 500 }
    )
  }
}

// POST: ê¸‰ì—¬ ê³„ì•½ ìƒì„±
export const POST: RequestHandler = async ({ request }) => {
  try {
