import { query } from '$lib/database/connection.js'
import type { SalaryContract, UpdateSalaryContractRequest } from '$lib/types/salary-contracts'
import { json } from '@sveltejs/kit'
import type { RequestHandler } from './$types'

// GET: íŠ¹ì • ê¸‰ì—¬ ê³„ì•½ ì¡°íšŒ
export const GET: RequestHandler = async ({ params }) => {
  try {
    const { id } = params

    const result = await query(
      `
			SELECT 
				sc.*,
				CONCAT(e.last_name, e.first_name) as employee_name,
				e.employee_id as employee_id_number,
				e.department,
				e.position,
				CASE 
					WHEN sc.end_date IS NULL THEN 'ë¬´ê¸°í•œ'
					ELSE TO_CHAR(sc.end_date, 'YYYY-MM-DD')
				END as contract_end_display,
				CASE 
					WHEN sc.status = 'active' AND sc.end_date IS NULL THEN 'ì§„í–‰ì¤‘ (ë¬´ê¸°í•œ)'
					WHEN sc.status = 'active' AND sc.end_date >= CURRENT_DATE THEN 'ì§„í–‰ì¤‘'
					WHEN sc.status = 'expired' OR sc.end_date < CURRENT_DATE THEN 'ë§Œë£Œë¨'
					ELSE sc.status
				END as status_display
			FROM salary_contracts sc
			JOIN employees e ON sc.employee_id = e.id
			WHERE sc.id = $1
		`,
      [id]
    )

    if (result.rows.length === 0) {
      return json(
        {
          success: false,
          error: 'ê¸‰ì—¬ ê³„ì•½ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'
        },
        { status: 404 }
      )
    }

    const contract = result.rows[0]

    // ë‚ ì§œë¥¼ KSTë¡œ ë³€í™˜
    const convertToKST = (dateString: string) => {
      if (!dateString) return null
      const date = new Date(dateString)
      // UTC+9 (KST)ë¡œ ë³€í™˜
      const kstDate = new Date(date.getTime() + 9 * 60 * 60 * 1000)
      return kstDate.toISOString().split('T')[0] // YYYY-MM-DD í˜•ì‹ìœ¼ë¡œ ë°˜í™˜
    }

    const salaryContract: SalaryContract = {
      id: contract.id,
      employeeId: contract.employee_id,
      startDate: convertToKST(contract.start_date),
      endDate: convertToKST(contract.end_date),
      annualSalary: parseFloat(contract.annual_salary),
      monthlySalary: parseFloat(contract.monthly_salary),
      contractType: contract.contract_type,
      status: contract.status,
      notes: contract.notes,
      createdAt: contract.created_at,
      updatedAt: contract.updated_at,
      createdBy: contract.created_by,
      employeeName: contract.employee_name,
      employeeIdNumber: contract.employee_id_number,
      department: contract.department,
      position: contract.position,
      contractEndDisplay: contract.contract_end_display,
      statusDisplay: contract.status_display
    }

    return json({
      success: true,
      data: salaryContract
    })
  } catch (error) {
    return json(
      {
        success: false,
        error: 'ê¸‰ì—¬ ê³„ì•½ ì¡°íšŒì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.'
      },
      { status: 500 }
    )
  }
}

